INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1037', N'Processor Optimization Removal or Modification of Security-critical Code', N'Base', N'Incomplete', N'The developer builds a security-critical protection mechanism into the software, but the processor optimizes the execution of the program such that the mechanism is removed or modified.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1038', N'Insecure Automated Optimizations', N'Class', N'Draft', N'The product uses a mechanism that automatically optimizes code, e.g. to improve a characteristic such as performance, but the optimizations can have an unintended side effect that might violate an intended security assumption.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1039', N'Automated Recognition Mechanism with Inadequate Detection or Handling of Adversarial Input Perturbations', N'Class', N'Incomplete', N'The product uses an automated mechanism such as machine learning to recognize complex data inputs (e.g. image or audio) as a particular concept or category, but it does not properly detect or handle inputs that have been modified or constructed in a way that causes the mechanism to detect a different, incorrect concept.', N'When techniques such as machine learning are used to automatically classify input streams, and those classifications are used for security-critical decisions, then any mistake in classification can introduce a vulnerability that allows attackers to cause the product to make the wrong security decision.  If the automated mechanism is not developed or "trained" with enough input data, then attackers may be able to craft malicious input that intentionally triggers the incorrect classification.
            Targeted technologies include, but are not necessarily limited to:
            
               automated speech recognition
               automated image recognition
            
            For example, an attacker might modify road signs or road surface markings to trick autonomous vehicles into misreading the sign/marking and performing a dangerous action.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-104', N'Struts: Form Bean Does Not Extend Validation Class', N'Variant', N'Draft', N'If a form bean does not extend an ActionForm subclass of the Validator framework, it can expose the application to other weaknesses related to insufficient input validation.', NULL, N'In order to use the Struts Validator, a form must extend one of the following: ValidatorForm, ValidatorActionForm, DynaValidatorActionForm, and DynaValidatorForm. You must extend one of these classes because the Struts Validator ties in to your application by implementing the validate() method in these classes. Forms derived from the ActionForm and DynaActionForm classes cannot use the Struts Validator.', NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1041', N'Use of Redundant Code', N'Base', N'Incomplete', N'The software has multiple functions, methods, procedures, macros, etc. that contain the same code.', N'This issue makes it more difficult to maintain the software, which indirectly affects security by making it more difficult or time-consuming to find and/or fix vulnerabilities.  For example, if there are two copies of the same code, the programmer might fix a weakness in one copy while forgetting to fix the same weakness in another copy.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1042', N'Static Member Data Element outside of a Singleton Class Element', N'Variant', N'Incomplete', N'The code contains a member element that is declared as static (but not final), in which its parent class element  is not a singleton class - that is, a class element that can be used only once in the ''to'' association of a Create action.', N'This issue can make the software perform more slowly.  If the relevant code is reachable by an attacker, then this performance problem might introduce a vulnerability.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1043', N'Data Element Aggregating an Excessively Large Number of Non-Primitive Elements', N'Base', N'Incomplete', N'The software uses a data element that has an excessively large number of sub-elements with non-primitive data types such as structures or aggregated objects.', N'This issue can make the software perform more slowly.  If the relevant code is reachable by an attacker, then this performance problem might introduce a vulnerability.
		 While the interpretation of "excessively large" may vary for each product or developer, CISQ recommends a default of 5 sub-elements.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1044', N'Architecture with Number of Horizontal Layers Outside of Expected Range', N'Base', N'Incomplete', N'The software''s architecture contains too many - or too few - horizontal layers.', N'This issue makes it more difficult to maintain the software, which indirectly affects security by making it more difficult or time-consuming to find and/or fix vulnerabilities.  It also might make it easier to introduce vulnerabilities.
		 While the interpretation of "expected range" may vary for each product or developer, CISQ recommends a default minimum of 4 layers and maximum of 8 layers.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1045', N'Parent Class with a Virtual Destructor and a Child Class without a Virtual Destructor', N'Variant', N'Incomplete', N'A parent class has a virtual destructor method, but the parent has a child class that does not have a virtual destructor.', N'This issue can prevent the software from running reliably, since the child might not perform essential destruction operations.  If the relevant code is reachable by an attacker, then this reliability problem might introduce a vulnerability, such as a memory leak (CWE-401).', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1046', N'Creation of Immutable Text Using String Concatenation', N'Base', N'Incomplete', N'The software creates an immutable text string using string concatenation operations.', N'This programming pattern can be inefficient in comparison with use of text buffer data elements.
This issue can make the software perform more slowly.  If the relevant code is reachable by an attacker, then this performance problem might introduce a vulnerability.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1047', N'Modules with Circular Dependencies', N'Base', N'Incomplete', N'The software contains modules in which one module has references that cycle back to itself, i.e., there are circular dependencies.', N'As an example, with Java, this weakness might indicate cycles between packages.
This issue makes it more difficult to maintain the software due to insufficient modularity, which indirectly affects security by making it more difficult or time-consuming to find and/or fix vulnerabilities.  It also might make it easier to introduce vulnerabilities.
This issue can prevent the software from running reliably.  If the relevant code is reachable by an attacker, then this reliability problem might introduce a vulnerability.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1048', N'Invokable Control Element with Large Number of Outward Calls', N'Base', N'Incomplete', N'The code contains callable control elements that         contain an excessively large number of references to other         application objects external to the context of the callable,         i.e. a Fan-Out value that is excessively large.', N'While the interpretation of "excessively large Fan-Out value" may vary for each product or developer, CISQ recommends a default of 5 referenced objects.
		 This issue makes it more difficult to maintain the software, which indirectly affects security by making it more difficult or time-consuming to find and/or fix vulnerabilities.  It also might make it easier to introduce vulnerabilities.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1049', N'Excessive Data Query Operations in a Large Data Table', N'Base', N'Incomplete', N'The software performs a data query with a large number of joins and sub-queries on a large data table.', N'This issue can make the software perform more slowly.  If the relevant code is reachable by an attacker, then this performance problem might introduce a vulnerability.
	 While the interpretation of "large data table" and "large number of joins or sub-queries" may vary for each product or developer, CISQ recommends a default of 1 million rows for a "large" data table, a default minimum of 5 joins, and a default minimum of 3 sub-queries.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-105', N'Struts: Form Field Without Validator', N'Variant', N'Draft', N'The application has a form field that is not validated by a corresponding validation form, which can introduce other weaknesses related to insufficient input validation.', N'Omitting validation for even a single input field may give attackers the leeway they need to compromise the application. Although J2EE applications are not generally susceptible to memory corruption attacks, if a J2EE application interfaces with native code that does not perform array bounds checking, an attacker may be able to use an input validation mistake in the J2EE application to launch a buffer overflow attack.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1050', N'Excessive Platform Resource Consumption within a Loop', N'Base', N'Incomplete', N'The software has a loop body or loop condition that contains a control element that directly or indirectly consumes platform resources, e.g. messaging, sessions, locks, or file descriptors.', N'This issue can make the software perform more slowly.  If an attacker can influence the number of iterations in the loop, then this performance problem might allow a denial of service by consuming more platform resources than intended.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1051', N'Initialization with Hard-Coded Network Resource Configuration Data', N'Base', N'Incomplete', N'The software initializes data using hard-coded values that act as	 as network resource identifiers.', N'This issue can prevent the software from running reliably, e.g. if it runs in an environment does not use the hard-coded network resource identifiers. If the relevant code is reachable by an attacker, then this reliability problem might introduce a vulnerability.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1052', N'Excessive Use of Hard-Coded Literals in Initialization', N'Base', N'Incomplete', N'The software initializes a data element using a hard-coded literal that is not a simple integer or static constant element.', N'This issue makes it more difficult to modify or maintain the software, which indirectly affects security by making it more difficult or time-consuming to find and/or fix vulnerabilities.  It also might make it easier to introduce vulnerabilities.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1053', N'Missing Documentation for Design', N'Base', N'Incomplete', N'The product does not have documentation that represents how it is designed.', N'This issue can make it more difficult to understand and maintain the code.  It can make it more difficult and time-consuming to detect and/or fix vulnerabilities.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1054', N'Invocation of a Control Element at an Unnecessarily Deep Horizontal Layer', N'Base', N'Incomplete', N'The code at one architectural layer invokes code that resides at a deeper layer than the adjacent layer, i.e., the invocation skips at least one layer, and the invoked code is not part of a vertical utility layer that can be referenced from any horizontal layer.', N'This issue makes it more difficult to understand and maintain the software, which indirectly affects security by making it more difficult or time-consuming to find and/or fix vulnerabilities.  It also might make it easier to introduce vulnerabilities.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1055', N'Multiple Inheritance from Concrete Classes', N'Base', N'Incomplete', N'The software contains a class with inheritance from more than one concrete class.', N'This issue makes it more difficult to maintain the software, which indirectly affects security by making it more difficult or time-consuming to find and/or fix vulnerabilities.  It also might make it easier to introduce vulnerabilities.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1056', N'Invokable Control Element with Variadic Parameters', N'Base', N'Incomplete', N'A named-callable or method control element has a signature that supports a variable (variadic) number of parameters or arguments.', N'This issue can prevent the software from running reliably.  If the relevant code is reachable by an attacker, then this reliability problem might introduce a vulnerability.
		 With variadic arguments, it can be difficult or inefficient for manual analysis to be certain of which function/method is being invoked.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1057', N'Data Access Operations Outside of Expected Data Manager Component', N'Base', N'Incomplete', N'The software uses a dedicated, central data manager component as required by design, but it contains code that performs data-access operations that do not use this data manager.', N'This issue can make the software perform more slowly than intended, since the intended central data manager may have been explicitly optimized for performance or other quality characteristics.  If the relevant code is reachable by an attacker, then this performance problem might introduce a vulnerability.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1058', N'Invokable Control Element in Multi-Thread Context with non-Final Static Storable or Member Element', N'Base', N'Incomplete', N'The code contains a function or method that		 operates in a multi-threaded environment but owns an unsafe non-final		                     static storable or member data element.', N'This issue can prevent the software from running reliably.  If the relevant code is reachable by an attacker, then this reliability problem might introduce a vulnerability.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1059', N'Incomplete Documentation', N'Class', N'Incomplete', N'The documentation, whether on paper or in electronic form, does not contain descriptions of all the relevant elements of the product, such as its usage, structure, interfaces, design, implementation, configuration, operation, etc.', N'This issue makes it more difficult to maintain the software, which indirectly affects security by making it more difficult or time-consuming to find and/or fix vulnerabilities.  It also might make it easier to introduce vulnerabilities.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-106', N'Struts: Plug-in Framework not in Use', N'Variant', N'Draft', N'When an application does not use an input validation framework such as the Struts Validator, there is a greater risk of introducing weaknesses related to insufficient input validation.', N'Unchecked input is the leading cause of vulnerabilities in J2EE applications. Unchecked input leads to cross-site scripting, process control, and SQL injection vulnerabilities, among others.
            Although J2EE applications are not generally susceptible to memory corruption attacks, if a J2EE application interfaces with native code that does not perform array bounds checking, an attacker may be able to use an input validation mistake in the J2EE application to launch a buffer overflow attack.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1060', N'Excessive Number of Inefficient Server-Side Data Accesses', N'Base', N'Incomplete', N'The software performs too many data queries without using efficient data processing functionality such as stored procedures.', N'This issue can make the software perform more slowly due to computational expense.  If the relevant code is reachable by an attacker, then this performance problem might introduce a vulnerability.
	 While the interpetation of "too many data queries" may vary for each product or developer, CISQ recommends a default maximum of 5 data queries for an inefficient function/procedure.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1061', N'Insufficient Encapsulation', N'Class', N'Incomplete', N'The software does not sufficiently hide the internal representation and implementation details of data or methods, which might allow external components or modules to modify data unexpectedly, invoke unexpected functionality, or introduce dependencies that the programmer did not intend.', N'This issue makes it more difficult to maintain the software, which indirectly affects security by making it more difficult or time-consuming to find and/or fix vulnerabilities.  It also might make it easier to introduce vulnerabilities.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1062', N'Parent Class with References to Child Class', N'Base', N'Incomplete', N'The code has a parent class that contains references to a child class, its methods, or its members.', N'This issue can prevent the software from running reliably.  If the relevant code is reachable by an attacker, then this reliability problem might introduce a vulnerability.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1063', N'Creation of Class Instance within a Static Code Block', N'Base', N'Incomplete', N'A static code block creates an instance of a class.', N'This pattern identifies situations where a storable data element or member data element is initialized with a value in a block of code which is declared as static.
This issue can make the software perform more slowly by performing initialization before it is needed.  If the relevant code is reachable by an attacker, then this performance problem might introduce a vulnerability.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1064', N'Invokable Control Element with Signature Containing an Excessive Number of Parameters', N'Base', N'Incomplete', N'The software contains a function, subroutine, or method whose signature has an unnecessarily large number of parameters/arguments.', N'This issue makes it more difficult to understand and/or maintain the software, which indirectly affects security by making it more difficult or time-consuming to find and/or fix vulnerabilities.  It also might make it easier to introduce vulnerabilities.
	 While the interpetation of "large number of parameters." may vary for each product or developer, CISQ recommends a default maximum of 7 parameters/arguments.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1065', N'Runtime Resource Management Control Element in a Component Built to Run on Application Servers', N'Base', N'Incomplete', N'The application uses deployed components from application servers, but it also uses low-level functions/methods for management of resources, instead of the API provided by the application server.', N'This issue can prevent the software from running reliably.  If the relevant code is reachable by an attacker, then this reliability problem might introduce a vulnerability.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1066', N'Missing Serialization Control Element', N'Base', N'Incomplete', N'The software contains a serializable data element that does not have an associated serialization method.', N'This issue can prevent the software from running reliably, e.g. by triggering an exeption.  If the relevant code is reachable by an attacker, then this reliability problem might introduce a vulnerability.
	 As examples, the serializable nature of a data element comes from a serializable SerializableAttribute attribute in .NET and the inheritance from the java.io.Serializable interface in Java.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1067', N'Excessive Execution of Sequential Searches of Data Resource', N'Base', N'Incomplete', N'The software contains a data query against an SQL table or view that is configured in a way that does not utilize an index and may cause sequential searches to be performed.', N'This issue can make the software perform more slowly.  If the relevant code is reachable by an attacker, then this performance problem might introduce a vulnerability.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1068', N'Inconsistency Between Implementation and Documented Design', N'Base', N'Incomplete', N'The implementation of the product is not consistent with the design as described within the relevant documentation.', N'This issue makes it more difficult to maintain the software due to inconsistencies, which indirectly affects security by making it more difficult or time-consuming to find and/or fix vulnerabilities.  It also might make it easier to introduce vulnerabilities.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1069', N'Empty Exception Block', N'Variant', N'Incomplete', N'An invokable code block contains an exception handling block that does not contain any code, i.e. is empty.', N'When an exception handling block (such as a Catch and Finally block) is used, but that block is empty, this can prevent the software from running reliably.  If the relevant code is reachable by an attacker, then this reliability problem might introduce a vulnerability.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-107', N'Struts: Unused Validation Form', N'Variant', N'Draft', N'An unused validation form indicates that validation logic is not up-to-date.', N'It is easy for developers to forget to update validation logic when they remove or rename action form mappings. One indication that validation logic is not being properly maintained is the presence of an unused validation form.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1070', N'Serializable Data Element Containing non-Serializable Item Elements', N'Base', N'Incomplete', N'The software contains a serializable, storable data element such as a field or member, but the data element contains member elements that are not serializable.', N'This issue can prevent the software from running reliably.  If the relevant code is reachable by an attacker, then this reliability problem might introduce a vulnerability.
 	 As examples, the serializable nature of a data element comes from a serializable SerializableAttribute attribute in .NET and the inheritance from the java.io.Serializable interface in Java.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1071', N'Empty Code Block', N'Variant', N'Incomplete', N'The source code contains a block that does not contain any code, i.e., the block is empty.', N'Empty code blocks can occur in the bodies of conditionals, function or method definitions, exception handlers, etc.  While an empty code block might be intentional, it might also indicate incomplete implementation, accidental code deletion, unexpected macro expansion, etc.  For some programming languages and constructs, an empty block might be allowed by the syntax, but the lack of any behavior within the block might violate a convention or API in such a way that it is an error.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1072', N'Data Resource Access without Use of Connection Pooling', N'Base', N'Incomplete', N'The software accesses a data resource through a database without using a connection pooling capability.', N'This issue can make the software perform more slowly, as connection pools allow connections to be reused without the overhead and time consumption of opening and closing a new connection.  If the relevant code is reachable by an attacker, then this performance problem might introduce a vulnerability.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1073', N'Non-SQL Invokable Control Element with Excessive Number of Data Resource Accesses', N'Base', N'Incomplete', N'The software contains a client with a function or method that contains a large number of data accesses/queries that are sent through a data manager, i.e., does not use efficient database capabilities.', N'This issue can make the software perform more slowly.  If the relevant code is reachable by an attacker, then this performance problem might introduce a vulnerability.
	 While the interpetation of "large number of data accesses/queries" may vary for each product or developer, CISQ recommends a default maximum of 2 data accesses per function/method.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1074', N'Class with Excessively Deep Inheritance', N'Base', N'Incomplete', N'A class has an inheritance level that is too high, i.e., it has a large number of parent classes.', N'This issue makes it more difficult to understand and maintain the software, which indirectly affects security by making it more difficult or time-consuming to find and/or fix vulnerabilities.  It also might make it easier to introduce vulnerabilities.
	 While the interpetation of "large number of parent classes" may vary for each product or developer, CISQ recommends a default maximum of 7 parent classes.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1075', N'Unconditional Control Flow Transfer outside of Switch Block', N'Base', N'Incomplete', N'The software performs unconditional control transfer (such as a "goto") in code outside of a branching structure such as a switch block.', N'This issue makes it more difficult to maintain the software, which indirectly affects security by making it more difficult or time-consuming to find and/or fix vulnerabilities.  It also might make it easier to introduce vulnerabilities.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1076', N'Insufficient Adherence to Expected Conventions', N'Class', N'Incomplete', N'The product''s architecture, source code, design, documentation, or other artifact does not follow required conventions.', N'This issue makes it more difficult to maintain the software, which indirectly affects security by making it more difficult or time-consuming to find and/or fix vulnerabilities.  It also might make it easier to introduce vulnerabilities.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1077', N'Floating Point Comparison with Incorrect Operator', N'Variant', N'Incomplete', N'The code performs a comparison such as an        equality test between two float (floating point) values, but        it uses comparison operators that do not account for the        possibility of loss of precision.', N'Numeric calculation using floating point values
	   can generate imprecise results because of rounding errors.
	   As a result, two different calculations might generate
	   numbers that are mathematically equal, but have slightly
	   different bit representations that do not translate to the
	   same mathematically-equal values.  As a result, an equality
	   test or other comparison might produce unexpected
	   results.
	   This issue can prevent the software from running reliably.  If the relevant code is reachable by an attacker, then this reliability problem might introduce a vulnerability.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1078', N'Inappropriate Source Code Style or Formatting', N'Class', N'Incomplete', N'The source code does not follow				desired style or formatting for indentation, white				space, comments, etc.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1079', N'Parent Class without Virtual Destructor Method', N'Base', N'Incomplete', N'A parent class contains one or more child classes, but the parent class does not have a virtual destructor method.', N'This issue can prevent the software from running reliably due to undefined or unexpected behaviors.  If the relevant code is reachable by an attacker, then this reliability problem might introduce a vulnerability.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-108', N'Struts: Unvalidated Action Form', N'Variant', N'Incomplete', N'Every Action Form must have a corresponding validation form.', N'If a Struts Action Form Mapping specifies a form, it must have a validation form defined under the Struts Validator.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1080', N'Source Code File with Excessive Number of Lines of Code', N'Base', N'Incomplete', N'A source code file has too many lines of code.', N'This issue makes it more difficult to understand and/or maintain the software, which indirectly affects security by making it more difficult or time-consuming to find and/or fix vulnerabilities.  It also might make it easier to introduce vulnerabilities.
		 Whilte the interpetation of "too many lines of code" may vary for each product or developer, CISQ recommends a default threshold value of 1000.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1082', N'Class Instance Self Destruction Control Element', N'Base', N'Incomplete', N'The code contains a class instance that calls the method or function to delete or destroy itself.', N'For example, in C++, "delete this" will cause the object to delete itself.
	 This issue can prevent the software from running reliably.  If the relevant code is reachable by an attacker, then this reliability problem might introduce a vulnerability.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1083', N'Data Access from Outside Expected Data Manager Component', N'Base', N'Incomplete', N'The software is intended to manage data access through a particular data manager component such as a relational or non-SQL database, but it contains code that performs data access operations without using that component.', N'When the software has a data access component, the design may be intended to handle all data access operations through that component.  If a data access operation is performed outside of that component, then this may indicate a violation of the intended design.
	 This issue can prevent the software from running reliably.  If the relevant code is reachable by an attacker, then this reliability problem might introduce a vulnerability.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1084', N'Invokable Control Element with Excessive File or Data Access Operations', N'Base', N'Incomplete', N'A function or method contains too many operations that utilize a data manager or file resource.', N'This issue makes it more difficult to maintain the software, which indirectly affects security by making it more difficult or time-consuming to find and/or fix vulnerabilities.  It also might make it easier to introduce vulnerabilities.
	 While the interpetation of "too many operations" may vary for each product or developer, CISQ recommends a default maximum of 7 operations for the same data manager or file.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1085', N'Invokable Control Element with Excessive Volume of Commented-out Code', N'Base', N'Incomplete', N'A function, method, procedure, etc. contains an excessive amount of code that has been commented out within its body.', N'This issue makes it more difficult to maintain the software, which indirectly affects security by making it more difficult or time-consuming to find and/or fix vulnerabilities.  It also might make it easier to introduce vulnerabilities.
		 While the interpetation of "excessive volume" may vary for each product or developer, CISQ recommends a default threshold of 2% of commented code.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1086', N'Class with Excessive Number of Child Classes', N'Base', N'Incomplete', N'A class contains an unnecessarily large number of children.', N'This issue makes it more difficult to understand and maintain the software, which indirectly affects security by making it more difficult or time-consuming to find and/or fix vulnerabilities.  It also might make it easier to introduce vulnerabilities.
	 While the interpetation of "large number of children" may vary for each product or developer, CISQ recommends a default maximum of 10 child classes.', NULL, NULL)
GO
