INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-110', N'Struts: Validator Without Form Field', N'Variant', N'Draft', N'Validation fields that do not appear in forms they are associated with indicate that the validation logic is out of date.', N'It is easy for developers to forget to update validation logic when they make changes to an ActionForm class. One indication that validation logic is not being properly maintained is inconsistencies between the action form and the validation form.
