INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1104', N'Use of Unmaintained Third Party Components', N'Base', N'Incomplete', N'The product relies on third-party components that are not actively supported or maintained by the original developer or a trusted proxy for the original developer.', N'Reliance on components that are no longer maintained can make it difficult or impossible to fix significant bugs, vulnerabilities, or quality issues. In effect, unmaintained code can become obsolete.
