INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-1109', N'Use of Same Variable for Multiple Purposes', N'Base', N'Incomplete', N'The code contains a callable, block, or other code element in which the same variable is used to control more than one unique task or store more than one instance of data.', N'Use of the same variable for multiple purposes can make it more difficult for a person to read or understand the code, potentially hiding other quality issues.
