INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-117', N'Improper Output Neutralization for Logs', N'Base', N'Draft', N'The software does not neutralize or incorrectly neutralizes output that is written to logs.', N'This can allow an attacker to forge log entries or inject malicious content into logs.
            Log forging vulnerabilities occur when:
            
               
