INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-144', N'Improper Neutralization of Line Delimiters', N'Variant', N'Draft', N'The software receives input from an upstream component, but it does not neutralize or incorrectly neutralizes special elements that could be interpreted as line delimiters when they are sent to a downstream component.', N'As data is parsed, an injected/absent/malformed delimiter may cause the process to take unexpected actions.', NULL, NULL)
