INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-145', N'Improper Neutralization of Section Delimiters', N'Variant', N'Incomplete', N'The software receives input from an upstream component, but it does not neutralize or incorrectly neutralizes special elements that could be interpreted as section delimiters when they are sent to a downstream component.', N'As data is parsed, an injected/absent/malformed delimiter may cause the process to take unexpected actions.
            One example of a section delimiter is the boundary string in a multipart MIME message. In many cases, doubled line delimiters can serve as a section delimiter.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-146', N'Improper Neutralization of Expression/Command Delimiters', N'Variant', N'Incomplete', N'The software receives input from an upstream component, but it does not neutralize or incorrectly neutralizes special elements that could be interpreted as expression or command delimiters when they are sent to a downstream component.', N'As data is parsed, an injected/absent/malformed delimiter may cause the process to take unexpected actions.', NULL, NULL)
