INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-147', N'Improper Neutralization of Input Terminators', N'Variant', N'Draft', N'The software receives input from an upstream component, but it does not neutralize or incorrectly neutralizes special elements that could be interpreted as input terminators when they are sent to a downstream component.', N'For example, a "." in SMTP signifies the end of mail message data, whereas a null character can be used for the end of a string.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-148', N'Improper Neutralization of Input Leaders', N'Variant', N'Draft', N'The application does not properly handle when a leading character or sequence ("leader") is missing or malformed, or if multiple leaders are used when only one should be allowed.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-149', N'Improper Neutralization of Quoting Syntax', N'Variant', N'Draft', N'Quotes injected into an application can be used to compromise a system. As data are parsed, an injected/absent/duplicate/malformed use of quotes may cause the process to take unexpected actions.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-15', N'External Control of System or Configuration Setting', N'Base', N'Incomplete', N'One or more system settings or configuration elements can be externally controlled by a user.', N'Allowing external control of system settings can disrupt service or cause an application to behave in unexpected, and potentially malicious ways.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-150', N'Improper Neutralization of Escape, Meta, or Control Sequences', N'Variant', N'Incomplete', N'The software receives input from an upstream component, but it does not neutralize or incorrectly neutralizes special elements that could be interpreted as escape, meta, or control character sequences when they are sent to a downstream component.', N'As data is parsed, an injected/absent/malformed delimiter may cause the process to take unexpected actions.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-151', N'Improper Neutralization of Comment Delimiters', N'Variant', N'Draft', N'The software receives input from an upstream component, but it does not neutralize or incorrectly neutralizes special elements that could be interpreted as comment delimiters when they are sent to a downstream component.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-152', N'Improper Neutralization of Macro Symbols', N'Variant', N'Draft', N'The software receives input from an upstream component, but it does not neutralize or incorrectly neutralizes special elements that could be interpreted as macro symbols when they are sent to a downstream component.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-153', N'Improper Neutralization of Substitution Characters', N'Variant', N'Draft', N'The software receives input from an upstream component, but it does not neutralize or incorrectly neutralizes special elements that could be interpreted as substitution characters when they are sent to a downstream component.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-154', N'Improper Neutralization of Variable Name Delimiters', N'Variant', N'Incomplete', N'The software receives input from an upstream component, but it does not neutralize or incorrectly neutralizes special elements that could be interpreted as variable name delimiters when they are sent to a downstream component.', N'As data is parsed, an injected delimiter may cause the process to take unexpected actions that result in an attack. Example: "$" for an environment variable.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-155', N'Improper Neutralization of Wildcards or Matching Symbols', N'Variant', N'Draft', N'The software receives input from an upstream component, but it does not neutralize or incorrectly neutralizes special elements that could be interpreted as wildcards or matching symbols when they are sent to a downstream component.', N'As data is parsed, an injected element may cause the process to take unexpected actions.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-156', N'Improper Neutralization of Whitespace', N'Variant', N'Draft', N'The software receives input from an upstream component, but it does not neutralize or incorrectly neutralizes special elements that could be interpreted as whitespace when they are sent to a downstream component.', N'This can include space, tab, etc.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-157', N'Failure to Sanitize Paired Delimiters', N'Variant', N'Draft', N'The software does not properly handle the characters that are used to mark the beginning and ending of a group of entities, such as parentheses, brackets, and braces.', N'Paired delimiters might include:
            
               
                  < and > angle brackets
                  ( and ) parentheses
                  { and } braces
                  [ and ] square brackets
                  " " double quotes
                  '' '' single quotes', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-158', N'Improper Neutralization of Null Byte or NUL Character', N'Variant', N'Incomplete', N'The software receives input from an upstream component, but it does not neutralize or incorrectly neutralizes NUL characters or null bytes when they are sent to a downstream component.', N'As data is parsed, an injected NUL character or null byte may cause the software to believe the input is terminated earlier than it actually is, or otherwise cause the input to be misinterpreted. This could then be used to inject potentially dangerous input that occurs after the null byte or otherwise bypass validation routines and other protection mechanisms.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-159', N'Failure to Sanitize Special Element', N'Class', N'Draft', N'Weaknesses in this attack-focused category do not properly filter and interpret special elements in user-controlled input which could cause adverse effect on the software behavior and integrity.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-16', N'Configuration', NULL, N'Draft', N'Weaknesses in this category are typically introduced during the configuration of the software.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-160', N'Improper Neutralization of Leading Special Elements', N'Variant', N'Incomplete', N'The software receives input from an upstream component, but it does not neutralize or incorrectly neutralizes leading special elements that could be interpreted in unexpected ways when they are sent to a downstream component.', N'As data is parsed, improperly handled leading special elements may cause the process to take unexpected actions that result in an attack.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-161', N'Improper Neutralization of Multiple Leading Special Elements', N'Variant', N'Incomplete', N'The software receives input from an upstream component, but it does not neutralize or incorrectly neutralizes multiple leading special elements that could be interpreted in unexpected ways when they are sent to a downstream component.', N'As data is parsed, improperly handled multiple leading special elements may cause the process to take unexpected actions that result in an attack.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-162', N'Improper Neutralization of Trailing Special Elements', N'Variant', N'Incomplete', N'The software receives input from an upstream component, but it does not neutralize or incorrectly neutralizes trailing special elements that could be interpreted in unexpected ways when they are sent to a downstream component.', N'As data is parsed, improperly handled trailing special elements may cause the process to take unexpected actions that result in an attack.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-163', N'Improper Neutralization of Multiple Trailing Special Elements', N'Variant', N'Incomplete', N'The software receives input from an upstream component, but it does not neutralize or incorrectly neutralizes multiple trailing special elements that could be interpreted in unexpected ways when they are sent to a downstream component.', N'As data is parsed, improperly handled multiple trailing special elements may cause the process to take unexpected actions that result in an attack.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-164', N'Improper Neutralization of Internal Special Elements', N'Variant', N'Incomplete', N'The software receives input from an upstream component, but it does not neutralize or incorrectly neutralizes internal special elements that could be interpreted in unexpected ways when they are sent to a downstream component.', N'As data is parsed, improperly handled internal special elements may cause the process to take unexpected actions that result in an attack.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-165', N'Improper Neutralization of Multiple Internal Special Elements', N'Variant', N'Incomplete', N'The software receives input from an upstream component, but it does not neutralize or incorrectly neutralizes multiple internal special elements that could be interpreted in unexpected ways when they are sent to a downstream component.', N'As data is parsed, improperly handled multiple internal special elements may cause the process to take unexpected actions that result in an attack.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-166', N'Improper Handling of Missing Special Element', N'Base', N'Draft', N'The software receives input from an upstream component, but it does not handle or incorrectly handles when an expected special element is missing.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-167', N'Improper Handling of Additional Special Element', N'Base', N'Draft', N'The software receives input from an upstream component, but it does not handle or incorrectly handles when an additional unexpected special element is missing.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-168', N'Improper Handling of Inconsistent Special Elements', N'Base', N'Draft', N'The software does not handle when an inconsistency exists between two or more special characters or reserved words.', N'An example of this problem would be if paired characters appear in the wrong order, or if the special characters are not properly nested.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-169', N'DEPRECATED: Technology-Specific Special Elements', NULL, N'Deprecated', N'This category has been deprecated. It was originally intended as a "catch-all" for input validation problems in technologies that did not have their own CWE, but introduces unnecessary depth to the hierarchy.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-17', N'DEPRECATED: Code', NULL, N'Deprecated', N'This entry has been deprecated.  It was originally used for organizing the Development View (CWE-699) and some other views, but it introduced unnecessary complexity and depth to the resulting tree.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-170', N'Improper Null Termination', N'Base', N'Incomplete', N'The software does not terminate or incorrectly terminates a string or array with a null character or equivalent terminator.', N'Null termination errors frequently occur in two different ways. An off-by-one error could cause a null to be written out of bounds, leading to an overflow. Or, a program could use a strncpy() function call incorrectly, which prevents a null terminator from being added at all. Other scenarios are possible.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-171', N'Cleansing, Canonicalization, and Comparison Errors', NULL, N'Draft', N'Weaknesses in this category are related to improper handling of data within protection mechanisms that attempt to perform neutralization for untrusted data.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-172', N'Encoding Error', N'Class', N'Draft', N'The software does not properly encode or decode the data, resulting in unexpected values.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-173', N'Improper Handling of Alternate Encoding', N'Variant', N'Draft', N'The software does not properly handle when an input uses an alternate encoding that is valid for the control sphere to which the input is being sent.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-174', N'Double Decoding of the Same Data', N'Variant', N'Draft', N'The software decodes the same input twice, which can limit the effectiveness of any protection mechanism that occurs in between the decoding operations.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-175', N'Improper Handling of Mixed Encoding', N'Variant', N'Draft', N'The software does not properly handle when the same input uses several different (mixed) encodings.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-176', N'Improper Handling of Unicode Encoding', N'Variant', N'Draft', N'The software does not properly handle when an input contains Unicode encoding.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-177', N'Improper Handling of URL Encoding (Hex Encoding)', N'Variant', N'Draft', N'The software does not properly handle when all or part of an input has been URL encoded.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-178', N'Improper Handling of Case Sensitivity', N'Base', N'Incomplete', N'The software does not properly account for differences in case sensitivity when accessing or determining the properties of a resource, leading to inconsistent results.', N'Improperly handled case sensitive data can lead to several possible consequences, including:
            
               
                  case-insensitive passwords reducing the size of the key space, making brute force attacks easier
                  bypassing filters or access controls using alternate names
                  multiple interpretation errors using alternate names.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-179', N'Incorrect Behavior Order: Early Validation', N'Base', N'Incomplete', N'The software validates input before applying protection mechanisms that modify the input, which could allow an attacker to bypass the validation via dangerous inputs that only arise after the modification.', N'Software needs to validate data at the proper time, after data has been canonicalized and cleansed. Early validation is susceptible to various manipulations that result in dangerous inputs that are produced by canonicalization and cleansing.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-18', N'DEPRECATED: Source Code', NULL, N'Deprecated', N'This entry has been deprecated.  It was originally used for organizing the Development View (CWE-699) and some other views, but it introduced unnecessary complexity and depth to the resulting tree.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-180', N'Incorrect Behavior Order: Validate Before Canonicalize', N'Base', N'Draft', N'The software validates input before it is canonicalized, which prevents the software from detecting data that becomes invalid after the canonicalization step.', N'This can be used by an attacker to bypass the validation and launch attacks that expose weaknesses that would otherwise be prevented, such as injection.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-181', N'Incorrect Behavior Order: Validate Before Filter', N'Base', N'Draft', N'The software validates data before it has been filtered, which prevents the software from detecting data that becomes invalid after the filtering step.', N'This can be used by an attacker to bypass the validation and launch attacks that expose weaknesses that would otherwise be prevented, such as injection.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-182', N'Collapse of Data into Unsafe Value', N'Base', N'Draft', N'The software filters data in a way that causes it to be reduced or "collapsed" into an unsafe value that violates an expected security property.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-183', N'Permissive Whitelist', N'Base', N'Draft', N'An application uses a "whitelist" of acceptable values, but the whitelist includes at least one unsafe value, leading to resultant weaknesses.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-184', N'Incomplete Blacklist', N'Base', N'Draft', N'An application uses a "blacklist" of prohibited values, but the blacklist is incomplete.', N'If an incomplete blacklist is used as a security mechanism, then the software may allow unintended values to pass into the application logic.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-185', N'Incorrect Regular Expression', N'Base', N'Draft', N'The software specifies a regular expression in a way that causes data to be improperly matched or compared.', N'When the regular expression is used in protection mechanisms such as filtering or validation, this may allow an attacker to bypass the intended restrictions on the incoming data.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-186', N'Overly Restrictive Regular Expression', N'Base', N'Draft', N'A regular expression is overly restrictive, which prevents dangerous values from being detected.', N'This weakness is not about regexp complexity. Rather It is about a regular expression that does not match all values that are intended. Consider the use of a regexp to whitelist acceptable values or to blacklist unwanted terms. An overly restrictive regexp misses some potentially security-relevant values leading to either false positives *or* false negatives, depending on how the regexp is being used within the code. Consider the expression /[0-8]/ where the intention was /[0-9]/.  This expression is not �complex� but the value �9� is not matched when maybe the programmer planned to check for it.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-187', N'Partial String Comparison', N'Variant', N'Incomplete', N'The software performs a comparison that only examines a portion of a factor before determining whether there is a match, such as a substring, leading to resultant weaknesses.', N'For example, an attacker might succeed in authentication by providing a small password that matches the associated portion of the larger, correct password.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-188', N'Reliance on Data/Memory Layout', N'Base', N'Draft', N'The software makes invalid assumptions about how protocol data or memory is organized at a lower level, resulting in unintended program behavior.', N'When changing platforms or protocol versions, in-memory organization of data may change in unintended ways. For example, some architectures may place local variables A and B right next to each other with A on top; some may place them next to each other with B on top; and others may add some padding to each. The padding size may vary to ensure that each variable is aligned to a proper word size.
            In protocol implementations, it is common to calculate an offset relative to another field to pick out a specific piece of data. Exceptional conditions, often involving new protocol versions, may add corner cases that change the data layout in an unusual way. The result can be that an implementation accesses an unintended field in the packet, treating data of one type as data of another type.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-189', N'Numeric Errors', NULL, N'Draft', N'Weaknesses in this category are related to improper calculation or conversion of numbers.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-19', N'Data Processing Errors', NULL, N'Draft', N'Weaknesses in this category are typically found in functionality that processes data.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-190', N'Integer Overflow or Wraparound', N'Base', N'Incomplete', N'The software performs a calculation that can produce an integer overflow or wraparound, when the logic assumes that the resulting value will always be larger than the original value. This can introduce other weaknesses when the calculation is used for resource management or execution control.', N'An integer overflow or wraparound occurs when an integer value is incremented to a value that is too large to store in the associated representation. When this occurs, the value may wrap to become a very small or negative number. While this may be intended behavior in circumstances that rely on wrapping, it can have security consequences if the wrap is unexpected. This is especially the case if the integer overflow can be triggered using user-supplied inputs. This becomes security-critical when the result is used to control looping, make a security decision, or determine the offset or size in behaviors such as memory allocation, copying, concatenation, etc.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-191', N'Integer Underflow (Wrap or Wraparound)', N'Base', N'Draft', N'The product subtracts one value from another, such that the result is less than the minimum allowable integer value, which produces a value that is not equal to the correct result.', N'This can happen in signed and unsigned cases.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-192', N'Integer Coercion Error', N'Class', N'Incomplete', N'Integer coercion refers to a set of flaws pertaining to the type casting, extension, or truncation of primitive data types.', N'Several flaws fall under the category of integer coercion errors. For the most part, these errors in and of themselves result only in availability and data integrity issues. However, in some circumstances, they may result in other, more complicated security related flaws, such as buffer overflow conditions.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-193', N'Off-by-one Error', N'Base', N'Draft', N'A product calculates or uses an incorrect maximum or minimum value that is 1 more, or 1 less, than the correct value.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-194', N'Unexpected Sign Extension', N'Base', N'Incomplete', N'The software performs an operation on a number that causes it to be sign extended when it is transformed into a larger data type. When the original number is negative, this can produce unexpected values that lead to resultant weaknesses.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-195', N'Signed to Unsigned Conversion Error', N'Variant', N'Draft', N'The software uses a signed primitive and performs a cast to an unsigned primitive, which can produce an unexpected value if the value of the signed primitive can not be represented using an unsigned primitive.', N'It is dangerous to rely on implicit casts between signed and unsigned numbers because the result can take on an unexpected value and violate assumptions made by the program.
            Often, functions will return negative values to indicate a failure. When the result of a function is to be used as a size parameter, using these negative return values can have unexpected results. For example, if negative size values are passed to the standard memory copy or allocation functions they will be implicitly cast to a large unsigned value. This may lead to an exploitable buffer overflow or underflow condition.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-196', N'Unsigned to Signed Conversion Error', N'Variant', N'Draft', N'The software uses an unsigned primitive and performs a cast to a signed primitive, which can produce an unexpected value if the value of the unsigned primitive can not be represented using a signed primitive.', N'Although less frequent an issue than signed-to-unsigned conversion, unsigned-to-signed conversion can be the perfect precursor to dangerous buffer underwrite conditions that allow attackers to move down the stack where they otherwise might not have access in a normal buffer overflow condition. Buffer underwrites occur frequently when large unsigned values are cast to signed values, and then used as indexes into a buffer or for pointer arithmetic.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-197', N'Numeric Truncation Error', N'Base', N'Incomplete', N'Truncation errors occur when a primitive is cast to a primitive of a smaller size and data is lost in the conversion.', N'When a primitive is cast to a smaller primitive, the high order bits of the large value are lost in the conversion, potentially resulting in an unexpected value that is not equal to the original value. This value may be required as an index into a buffer, a loop iterator, or simply necessary state data. In any case, the value cannot be trusted and the system will be in an undefined state. While this method may be employed viably to isolate the low bits of a value, this usage is rare, and truncation usually implies that an implementation error has occurred.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-198', N'Use of Incorrect Byte Ordering', N'Base', N'Draft', N'The software receives input from an upstream component, but it does not account for byte ordering (e.g. big-endian and little-endian) when processing the input, causing an incorrect number or value to be used.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-199', N'Information Management Errors', NULL, N'Draft', N'Weaknesses in this category are related to improper handling of sensitive information.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-2', N'7PK - Environment', NULL, N'Draft', N'This category represents one of the phyla in the Seven Pernicious Kingdoms vulnerability classification. It includes weaknesses that are typically introduced during unexpected environmental conditions. According to the authors of the Seven Pernicious Kingdoms, "This section includes everything that is outside of the source code but is still critical to the security of the product that is being created. Because the issues covered by this kingdom are not directly related to source code, we separated it from the rest of the kingdoms."', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-20', N'Improper Input Validation', N'Class', N'Usable', N'The product does not validate or incorrectly validates input that can affect the control flow or data flow of a program.', N'When software does not validate input properly, an attacker is able to craft the input in a form that is not expected by the rest of the application. This will lead to parts of the system receiving unintended input, which may result in altered control flow, arbitrary control of a resource, or arbitrary code execution.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-200', N'Information Exposure', N'Class', N'Incomplete', N'An information exposure is the intentional or unintentional disclosure of information to an actor that is not explicitly authorized to have access to that information.', N'The information either:
            
               
                  is regarded as sensitive within the product''s own functionality, such as a private message; or
                  provides information about the product or its environment that could be useful in an attack but is normally not available to the attacker, such as the installation path of a product that is remotely accessible.
               
            
            Many information exposures are resultant (e.g. PHP script error revealing the full path of the program), but they can also be primary (e.g. timing discrepancies in cryptography). There are many different types of problems that involve information exposures. Their severity can range widely depending on the type of information that is revealed.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-201', N'Information Exposure Through Sent Data', N'Variant', N'Draft', N'The accidental exposure of sensitive information through sent data refers to the transmission of data which are either sensitive in and of itself or useful in the further exploitation of the system through standard data channels.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-202', N'Exposure of Sensitive Data Through Data Queries', N'Variant', N'Draft', N'When trying to keep information confidential, an attacker can often infer some of the information by using statistics.', N'In situations where data should not be tied to individual users, but a large number of users should be able to make queries that "scrub" the identity of users, it may be possible to get information about a user -- e.g., by specifying search terms that are known to be unique to that user.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-203', N'Information Exposure Through Discrepancy', N'Base', N'Incomplete', N'The product behaves differently or sends different responses in a way that exposes security-relevant information about the state of the product, such as whether a particular operation was successful or not.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-204', N'Response Discrepancy Information Exposure', N'Base', N'Incomplete', N'The software provides different responses to incoming requests in a way that allows an actor to determine system state information that is outside of that actor''s control sphere.', N'This issue frequently occurs during authentication, where a difference in failed-login messages could allow an attacker to determine if the username is valid or not. These exposures can be inadvertent (bug) or intentional (design).', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-205', N'Information Exposure Through Behavioral Discrepancy', N'Base', N'Incomplete', N'The product''s actions indicate important differences based on (1) the internal state of the product or (2) differences from other products in the same class.', N'For example, attacks such as OS fingerprinting rely heavily on both behavioral and response discrepancies.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-206', N'Information Exposure of Internal State Through Behavioral Inconsistency', N'Variant', N'Incomplete', N'Two separate operations in a product cause the product to behave differently in a way that is observable to an attacker and reveals security-relevant information about the internal state of the product, such as whether a particular operation was successful or not.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-207', N'Information Exposure Through an External Behavioral Inconsistency', N'Variant', N'Draft', N'The product behaves differently than other products like it, in a way that is observable to an attacker and exposes security-relevant information about which product is being used.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-208', N'Information Exposure Through Timing Discrepancy', N'Base', N'Incomplete', N'Two separate operations in a product require different amounts of time to complete, in a way that is observable to an actor and reveals security-relevant information about the state of the product, such as whether a particular operation was successful or not.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-209', N'Information Exposure Through an Error Message', N'Base', N'Draft', N'The software generates an error message that includes sensitive information about its environment, users, or associated data.', N'The sensitive information may be valuable information on its own (such as a password), or it may be useful for launching other, more deadly attacks. If an attack fails, an attacker may use error information provided by the server to launch another more focused attack. For example, an attempt to exploit a path traversal weakness (CWE-22) might yield the full pathname of the installed application. In turn, this could be used to select the proper number of ".." sequences to navigate to the targeted file. An attack using SQL injection (CWE-89) might not initially succeed, but an error message could reveal the malformed query, which would expose query logic and possibly even passwords or other sensitive information used within the query.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-21', N'Pathname Traversal and Equivalence Errors', NULL, N'Incomplete', N'Weaknesses in this category can be used to access files outside of a restricted directory (path traversal) or to perform operations on files that would otherwise be restricted (path equivalence). Files, directories, and folders are so central to information technology that many different weaknesses and variants have been discovered. The manipulations generally involve special characters or sequences in pathnames, or the use of alternate references or channels.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-210', N'Information Exposure Through Self-generated Error Message', N'Base', N'Draft', N'The software identifies an error condition and creates its own diagnostic or error messages that contain sensitive information.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-211', N'Information Exposure Through Externally-Generated Error Message', N'Base', N'Incomplete', N'The software performs an operation that triggers an external diagnostic or error message that is not directly generated by the software, such as an error generated by the programming language interpreter that the software uses. The error can contain sensitive system information.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-212', N'Improper Cross-boundary Removal of Sensitive Data', N'Base', N'Incomplete', N'The software uses a resource that contains sensitive data, but it does not properly remove that data before it stores, transfers, or shares the resource with actors in another control sphere.', N'Resources that may contain sensitive data include documents, packets, messages, databases, etc. While this data may be useful to an individual user or small set of users who share the resource, it may need to be removed before the resource can be shared outside of the trusted group. The process of removal is sometimes called cleansing or scrubbing.
            For example, software that is used for editing documents might not remove sensitive data such as reviewer comments or the local pathname where the document is stored. Or, a proxy might not remove an internal IP address from headers before making an outgoing request to an Internet site.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-213', N'Intentional Information Exposure', N'Base', N'Draft', N'A product''s design or configuration explicitly requires the publication of information that could be regarded as sensitive by an administrator.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-214', N'Information Exposure Through Process Environment', N'Variant', N'Incomplete', N'A process is invoked with sensitive arguments, environment variables, or other elements that can be seen by other processes on the operating system.', N'Many operating systems allow a user to list information about processes that are owned by other users. This information could include command line arguments or environment variable settings. When this data contains sensitive information such as credentials, it might allow other users to launch an attack against the software or related resources.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-215', N'Information Exposure Through Debug Information', N'Variant', N'Draft', N'The application contains debugging code that can expose sensitive information to untrusted parties.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-216', N'Containment Errors (Container Errors)', N'Class', N'Incomplete', N'This tries to cover various problems in which improper data are included within a "container."', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-217', N'DEPRECATED: Failure to Protect Stored Data from Modification', N'Base', N'Deprecated', N'This weakness has been deprecated because it incorporated and confused multiple weaknesses. The issues formerly covered in this weakness can be found at CWE-766 and CWE-767.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-218', N'DEPRECATED (Duplicate): Failure to provide confidentiality for stored data', N'Base', N'Deprecated', N'This weakness has been deprecated because it was a duplicate of CWE-493. All content has been transferred to CWE-493.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-219', N'Sensitive Data Under Web Root', N'Variant', N'Draft', N'The application stores sensitive data under the web document root with insufficient access control, which might make it accessible to untrusted parties.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-22', N'Improper Limitation of a Pathname to a Restricted Directory (''Path Traversal'')', N'Base', N'Draft', N'The software uses external input to construct a pathname that is intended to identify a file or directory that is located underneath a restricted parent directory, but the software does not properly neutralize special elements within the pathname that can cause the pathname to resolve to a location that is outside of the restricted directory.', N'Many file operations are intended to take place within a restricted directory. By using special elements such as ".." and "/" separators, attackers can escape outside of the restricted location to access files or directories that are elsewhere on the system. One of the most common special elements is the "../" sequence, which in most modern operating systems is interpreted as the parent directory of the current location. This is referred to as relative path traversal. Path traversal also covers the use of absolute pathnames such as "/usr/local/bin", which may also be useful in accessing unexpected files. This is referred to as absolute path traversal.
            In many programming languages, the injection of a null byte (the 0 or NUL) may allow an attacker to truncate a generated filename to widen the scope of attack. For example, the software may add ".txt" to any pathname, thus limiting the attacker to text files, but a null injection may effectively remove this restriction.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-220', N'Sensitive Data Under FTP Root', N'Variant', N'Draft', N'The application stores sensitive data under the FTP document root with insufficient access control, which might make it accessible to untrusted parties.', NULL, N'Various Unix FTP servers require a password file that is under the FTP root, due to use of chroot.', NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-221', N'Information Loss or Omission', N'Class', N'Incomplete', N'The software does not record, or improperly records, security-relevant information that leads to an incorrect decision or hampers later analysis.', N'This can be resultant, e.g. a buffer overflow might trigger a crash before the product can log the event.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-222', N'Truncation of Security-relevant Information', N'Base', N'Draft', N'The application truncates the display, recording, or processing of security-relevant information in a way that can obscure the source or nature of an attack.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-223', N'Omission of Security-relevant Information', N'Base', N'Draft', N'The application does not record or display information that would be important for identifying the source or nature of an attack, or determining if an action is safe.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-224', N'Obscured Security-relevant Information by Alternate Name', N'Base', N'Incomplete', N'The software records security-relevant information according to an alternate name of the affected entity, instead of the canonical name.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-225', N'DEPRECATED (Duplicate): General Information Management Problems', N'Base', N'Deprecated', N'This weakness can be found at CWE-199.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-226', N'Sensitive Information Uncleared Before Release', N'Base', N'Draft', N'The software does not fully clear previously used information in a data structure, file, or other resource, before making that resource available to a party in another control sphere.', N'This typically results from new data that is not as long as the old data, which leaves portions of the old data still available. Equivalent errors can occur in other situations where the length of data is variable but the associated data structure is not. If memory is not cleared after use, it may allow unintended actors to read the data when the memory is reallocated.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-227', N'7PK - API Abuse', NULL, N'Draft', N'This category represents one of the phyla in the Seven Pernicious Kingdoms vulnerability classification. It includes weaknesses that involve the software useing an API in a manner contrary to its intended use. According to the authors of the Seven Pernicious Kingdoms, "An API is a contract between a caller and a callee. The most common forms of API misuse occurs when the caller does not honor its end of this contract. For example, if a program does not call chdir() after calling chroot(), it violates the contract that specifies how to change the active root directory in a secure fashion. Another good example of library abuse is expecting the callee to return trustworthy DNS information to the caller. In this case, the caller misuses the callee API by making certain assumptions about its behavior (that the return value can be used for authentication purposes). One can also violate the caller-callee contract from the other side. For example, if a coder subclasses SecureRandom and returns a non-random value, the contract is violated."', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-228', N'Improper Handling of Syntactically Invalid Structure', N'Class', N'Incomplete', N'The product does not handle or incorrectly handles input that is not syntactically well-formed with respect to the associated specification.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-229', N'Improper Handling of Values', N'Base', N'Incomplete', N'The software does not properly handle when the expected number of values for parameters, fields, or arguments is not provided in input, or if those values are undefined.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-23', N'Relative Path Traversal', N'Base', N'Draft', N'The software uses external input to construct a pathname that should be within a restricted directory, but it does not properly neutralize sequences such as ".." that can resolve to a location that is outside of that directory.', N'This allows attackers to traverse the file system to access files or directories that are outside of the restricted directory.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-230', N'Improper Handling of Missing Values', N'Variant', N'Draft', N'The software does not handle or incorrectly handles when a parameter, field, or argument name is specified, but the associated value is missing, i.e. it is empty, blank, or null.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-231', N'Improper Handling of Extra Values', N'Variant', N'Draft', N'The software does not handle or incorrectly handles when more values are provided than expected.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-232', N'Improper Handling of Undefined Values', N'Variant', N'Draft', N'The software does not handle or incorrectly handles when a value is not defined or supported for the associated parameter, field, or argument name.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-233', N'Improper Handling of Parameters', N'Base', N'Incomplete', N'The software does not properly handle when the expected number of parameters, fields, or arguments is not provided in input, or if those parameters are undefined.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-234', N'Failure to Handle Missing Parameter', N'Variant', N'Incomplete', N'If too few arguments are sent to a function, the function will still pop the expected number of arguments from the stack. Potentially, a variable number of arguments could be exhausted in a function as well.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-235', N'Improper Handling of Extra Parameters', N'Variant', N'Draft', N'The software does not handle or incorrectly handles when the number of parameters, fields, or arguments with the same name exceeds the expected amount.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-236', N'Improper Handling of Undefined Parameters', N'Variant', N'Draft', N'The software does not handle or incorrectly handles when a particular parameter, field, or argument name is not defined or supported by the product.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-237', N'Improper Handling of Structural Elements', N'Base', N'Incomplete', N'The software does not handle or incorrectly handles inputs that are related to complex structures.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-238', N'Improper Handling of Incomplete Structural Elements', N'Variant', N'Draft', N'The software does not handle or incorrectly handles when a particular structural element is not completely specified.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-239', N'Failure to Handle Incomplete Element', N'Variant', N'Draft', N'The software does not properly handle when a particular element is not completely specified.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-24', N'Path Traversal: ''../filedir''', N'Variant', N'Incomplete', N'The software uses external input to construct a pathname that should be within a restricted directory, but it does not properly neutralize "../" sequences that can resolve to a location that is outside of that directory.', N'This allows attackers to traverse the file system to access files or directories that are outside of the restricted directory.
            The "../" manipulation is the canonical manipulation for operating systems that use "/" as directory separators, such as UNIX- and Linux-based systems. In some cases, it is useful for bypassing protection schemes in environments for which "/" is supported but not the primary separator, such as Windows, which uses "\" but can also accept "/".', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-240', N'Improper Handling of Inconsistent Structural Elements', N'Base', N'Draft', N'The software does not handle or incorrectly handles when two or more structural elements should be consistent, but are not.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-241', N'Improper Handling of Unexpected Data Type', N'Base', N'Draft', N'The software does not handle or incorrectly handles when a particular element is not the expected type, e.g. it expects a digit (0-9) but is provided with a letter (A-Z).', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-242', N'Use of Inherently Dangerous Function', N'Base', N'Draft', N'The program calls a function that can never be guaranteed to work safely.', N'Certain functions behave in dangerous ways regardless of how they are used. Functions in this category were often implemented without taking security concerns into account. The gets() function is unsafe because it does not perform bounds checking on the size of its input. An attacker can easily send arbitrarily-sized input to gets() and overflow the destination buffer. Similarly, the >> operator is unsafe to use when reading into a statically-allocated character array because it does not perform bounds checking on the size of its input. An attacker can easily send arbitrarily-sized input to the >> operator and overflow the destination buffer.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-243', N'Creation of chroot Jail Without Changing Working Directory', N'Variant', N'Draft', N'The program uses the chroot() system call to create a jail, but does not change the working directory afterward. This does not prevent access to files outside of the jail.', N'Improper use of chroot() may allow attackers to escape from the chroot jail. The chroot() function call does not change the process''s current working directory, so relative paths may still refer to file system resources outside of the chroot jail after chroot() has been called.', N'The chroot() system call allows a process to change its perception of the root directory of the file system. After properly invoking chroot(), a process cannot access any files outside the directory tree defined by the new root directory. Such an environment is called a chroot jail and is commonly used to prevent the possibility that a processes could be subverted and used to access unauthorized files. For instance, many FTP servers run in chroot jails to prevent an attacker who discovers a new vulnerability in the server from being able to download the password file or other sensitive files on the system.', NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-244', N'Improper Clearing of Heap Memory Before Release (''Heap Inspection'')', N'Variant', N'Draft', N'Using realloc() to resize buffers that store sensitive information can leave the sensitive information exposed to attack, because it is not removed from memory.', N'When sensitive data such as a password or an encryption key is not removed from memory, it could be exposed to an attacker using a "heap inspection" attack that reads the sensitive data using memory dumps or other methods. The realloc() function is commonly used to increase the size of a block of allocated memory. This operation often requires copying the contents of the old memory block into a new and larger block. This operation leaves the contents of the original block intact but inaccessible to the program, preventing the program from being able to scrub sensitive data from memory. If an attacker can later examine the contents of a memory dump, the sensitive data could be exposed.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-245', N'J2EE Bad Practices: Direct Management of Connections', N'Variant', N'Draft', N'The J2EE application directly manages connections, instead of using the container''s connection management facilities.', N'The J2EE standard forbids the direct management of connections. It requires that applications use the container''s resource management facilities to obtain connections to resources. Every major web application container provides pooled database connection management as part of its resource management framework. Duplicating this functionality in an application is difficult and error prone, which is part of the reason it is forbidden under the J2EE standard.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-246', N'J2EE Bad Practices: Direct Use of Sockets', N'Variant', N'Draft', N'The J2EE application directly uses sockets instead of using framework method calls.', N'The J2EE standard permits the use of sockets only for the purpose of communication with legacy systems when no higher-level protocol is available. Authoring your own communication protocol requires wrestling with difficult security issues.
            Without significant scrutiny by a security expert, chances are good that a custom communication protocol will suffer from security problems. Many of the same issues apply to a custom implementation of a standard protocol. While there are usually more resources available that address security concerns related to implementing a standard protocol, these resources are also available to attackers.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-247', N'DEPRECATED (Duplicate): Reliance on DNS Lookups in a Security Decision', N'Base', N'Deprecated', N'This entry has been deprecated because it was a duplicate of CWE-350. All content has been transferred to CWE-350.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-248', N'Uncaught Exception', N'Base', N'Draft', N'An exception is thrown from a function, but it is not caught.', N'When an exception is not caught, it may cause the program to crash or expose sensitive information.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-249', N'DEPRECATED: Often Misused: Path Manipulation', N'Variant', N'Deprecated', N'This entry has been deprecated because of name confusion and an accidental combination of multiple weaknesses. Most of its content has been transferred to CWE-785.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-25', N'Path Traversal: ''/../filedir''', N'Variant', N'Incomplete', N'The software uses external input to construct a pathname that should be within a restricted directory, but it does not properly neutralize "/../" sequences that can resolve to a location that is outside of that directory.', N'This allows attackers to traverse the file system to access files or directories that are outside of the restricted directory.
            Sometimes a program checks for "../" at the beginning of the input, so a "/../" can bypass that check.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-250', N'Execution with Unnecessary Privileges', N'Class', N'Draft', N'The software performs an operation at a privilege level that is higher than the minimum level required, which creates new weaknesses or amplifies the consequences of other weaknesses.', N'New weaknesses can be exposed because running with extra privileges, such as root or Administrator, can disable the normal security checks being performed by the operating system or surrounding environment. Other pre-existing weaknesses can turn into security vulnerabilities if they occur while operating at raised privileges.
            Privilege management functions can behave in some less-than-obvious ways, and they have different quirks on different platforms. These inconsistencies are particularly pronounced if you are transitioning from one non-root user to another. Signal handlers and spawned processes run at the privilege of the owning process, so if a process is running as root when a signal fires or a sub-process is executed, the signal handler or sub-process will operate with root privileges.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-251', N'Often Misused: String Management', NULL, N'Incomplete', N'Functions that manipulate strings encourage buffer overflows.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-252', N'Unchecked Return Value', N'Base', N'Draft', N'The software does not check the return value from a method or function, which can prevent it from detecting unexpected states and conditions.', N'Two common programmer assumptions are "this function call can never fail" and "it doesn''t matter if this function call fails". If an attacker can force the function to fail or otherwise return a value that is not expected, then the subsequent program logic could lead to a vulnerability, because the software is not in a state that the programmer assumes. For example, if the program calls a function to drop privileges but does not check the return code to ensure that privileges were successfully dropped, then the program will continue to operate with the higher privileges.', N'Many functions will return some value about the success of their actions. This will alert the program whether or not to handle any errors caused by that function.', NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-253', N'Incorrect Check of Function Return Value', N'Base', N'Incomplete', N'The software incorrectly checks a return value from a function, which prevents the software from detecting errors or exceptional conditions.', N'Important and common functions will return some value about the success of its actions. This will alert the program whether or not to handle any errors caused by that function.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-254', N'7PK - Security Features', NULL, N'Incomplete', N'Software security is not security software. Here we''re concerned with topics like authentication, access control, confidentiality, cryptography, and privilege management.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-255', N'Credentials Management', NULL, N'Draft', N'Weaknesses in this category are related to the management of credentials.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-256', N'Unprotected Storage of Credentials', N'Base', N'Incomplete', N'Storing a password in plaintext may result in a system compromise.', N'Password management issues occur when a password is stored in plaintext in an application''s properties or configuration file. Storing a plaintext password in a configuration file allows anyone who can read the file access to the password-protected resource.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-257', N'Storing Passwords in a Recoverable Format', N'Base', N'Incomplete', N'The storage of passwords in a recoverable format makes them subject to password reuse attacks by malicious users. In fact, it should be noted that recoverable encrypted passwords provide no significant benefit over plaintext passwords since they are subject not only to reuse by malicious attackers but also by malicious insiders. If a system administrator can recover a password directly, or use a brute force search on the available information, the administrator can use the password on other accounts.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-258', N'Empty Password in Configuration File', N'Variant', N'Incomplete', N'Using an empty string as a password is insecure.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-259', N'Use of Hard-coded Password', N'Base', N'Draft', N'The software contains a hard-coded password, which it uses for its own inbound authentication or for outbound communication to external components.', N'A hard-coded password typically leads to a significant authentication failure that can be difficult for the system administrator to detect. Once detected, it can be difficult to fix, so the administrator may be forced into disabling the product entirely. There are two main variations:
            
               Inbound: the software contains an authentication mechanism that checks for a hard-coded password.
               Outbound: the software connects to another system or component, and it contains hard-coded password for connecting to that component.
            
            In the Inbound variant, a default administration account is created, and a simple password is hard-coded into the product and associated with that account. This hard-coded password is the same for each installation of the product, and it usually cannot be changed or disabled by system administrators without manually modifying the program, or otherwise patching the software. If the password is ever discovered or published (a common occurrence on the Internet), then anybody with knowledge of this password can access the product. Finally, since all installations of the software will have the same password, even across different organizations, this enables massive attacks such as worms to take place.
            The Outbound variant applies to front-end systems that authenticate with a back-end service. The back-end service may require a fixed password which can be easily discovered. The programmer may simply hard-code those back-end credentials into the front-end software. Any user of that program may be able to extract the password. Client-side systems with hard-coded passwords pose even more of a threat, since the extraction of a password from a binary is usually very simple.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-26', N'Path Traversal: ''/dir/../filename''', N'Variant', N'Draft', N'The software uses external input to construct a pathname that should be within a restricted directory, but it does not properly neutralize "/dir/../filename" sequences that can resolve to a location that is outside of that directory.', N'This allows attackers to traverse the file system to access files or directories that are outside of the restricted directory.
            The ''/dir/../filename'' manipulation is useful for bypassing some path traversal protection schemes. Sometimes a program only checks for "../" at the beginning of the input, so a "/../" can bypass that check.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-260', N'Password in Configuration File', N'Variant', N'Incomplete', N'The software stores a password in a configuration file that might be accessible to actors who do not know the password.', N'This can result in compromise of the system for which the password is used. An attacker could gain access to this file and learn the stored password or worse yet, change the password to one of their choosing.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-261', N'Weak Cryptography for Passwords', N'Variant', N'Incomplete', N'Obscuring a password with a trivial encoding does not protect the password.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-262', N'Not Using Password Aging', N'Variant', N'Draft', N'If no mechanism is in place for managing password aging, users will have no incentive to update passwords in a timely manner.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-263', N'Password Aging with Long Expiration', N'Base', N'Draft', N'Allowing password aging to occur unchecked can result in the possibility of diminished password integrity.', N'Just as neglecting to include functionality for the management of password aging is dangerous, so is allowing password aging to continue unchecked. Passwords must be given a maximum life span, after which a user is required to update with a new and different password.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-264', N'Permissions, Privileges, and Access Controls', NULL, N'Incomplete', N'Weaknesses in this category are related to the management of permissions, privileges, and other security features that are used to perform access control.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-265', N'Privilege / Sandbox Issues', NULL, N'Incomplete', N'Weaknesses in this category occur with improper enforcement of sandbox environments, or the improper handling, assignment, or management of privileges.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-266', N'Incorrect Privilege Assignment', N'Base', N'Draft', N'A product incorrectly assigns a privilege to a particular actor, creating an unintended sphere of control for that actor.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-267', N'Privilege Defined With Unsafe Actions', N'Base', N'Incomplete', N'A particular privilege, role, capability, or right can be used to perform unsafe actions that were not intended, even when it is assigned to the correct entity.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-268', N'Privilege Chaining', N'Base', N'Draft', N'Two distinct privileges, roles, capabilities, or rights can be combined in a way that allows an entity to perform unsafe actions that would not be allowed without that combination.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-269', N'Improper Privilege Management', N'Class', N'Incomplete', N'The software does not properly assign, modify, track, or check privileges for an actor, creating an unintended sphere of control for that actor.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-27', N'Path Traversal: ''dir/../../filename''', N'Variant', N'Draft', N'The software uses external input to construct a pathname that should be within a restricted directory, but it does not properly neutralize multiple internal "../" sequences that can resolve to a location that is outside of that directory.', N'This allows attackers to traverse the file system to access files or directories that are outside of the restricted directory.
            The ''directory/../../filename'' manipulation is useful for bypassing some path traversal protection schemes. Sometimes a program only removes one "../" sequence, so multiple "../" can bypass that check. Alternately, this manipulation could be used to bypass a check for "../" at the beginning of the pathname, moving up more than one directory level.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-270', N'Privilege Context Switching Error', N'Base', N'Draft', N'The software does not properly manage privileges while it is switching between different contexts that have different privileges or spheres of control.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-271', N'Privilege Dropping / Lowering Errors', N'Class', N'Incomplete', N'The software does not drop privileges before passing control of a resource to an actor that does not have those privileges.', N'In some contexts, a system executing with elevated permissions will hand off a process/file/etc. to another process or user. If the privileges of an entity are not reduced, then elevated privileges are spread throughout a system and possibly to an attacker.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-272', N'Least Privilege Violation', N'Base', N'Incomplete', N'The elevated privilege level required to perform operations such as chroot() should be dropped immediately after the operation is performed.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-273', N'Improper Check for Dropped Privileges', N'Base', N'Incomplete', N'The software attempts to drop privileges but does not check or incorrectly checks to see if the drop succeeded.', N'If the drop fails, the software will continue to run with the raised privileges, which might provide additional access to unprivileged users.', N'In Windows based environments that have access control, impersonation is used so that access checks can be performed on a client identity by a server with higher privileges. By impersonating the client, the server is restricted to client-level security -- although in different threads it may have much higher privileges.', NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-274', N'Improper Handling of Insufficient Privileges', N'Base', N'Draft', N'The software does not handle or incorrectly handles when it has insufficient privileges to perform an operation, leading to resultant weaknesses.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-275', N'Permission Issues', NULL, N'Draft', N'Weaknesses in this category are related to improper assignment or handling of permissions.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-276', N'Incorrect Default Permissions', N'Base', N'Draft', N'The software, upon installation, sets incorrect permissions for an object that exposes it to an unintended actor.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-277', N'Insecure Inherited Permissions', N'Variant', N'Draft', N'A product defines a set of insecure permissions that are inherited by objects that are created by the program.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-278', N'Insecure Preserved Inherited Permissions', N'Variant', N'Incomplete', N'A product inherits a set of insecure permissions for an object, e.g. when copying from an archive file, without user awareness or involvement.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-279', N'Incorrect Execution-Assigned Permissions', N'Variant', N'Draft', N'While it is executing, the software sets the permissions of an object in a way that violates the intended permissions that have been specified by the user.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-28', N'Path Traversal: ''..\filedir''', N'Variant', N'Incomplete', N'The software uses external input to construct a pathname that should be within a restricted directory, but it does not properly neutralize "..\" sequences that can resolve to a location that is outside of that directory.', N'This allows attackers to traverse the file system to access files or directories that are outside of the restricted directory.
            The ''..\'' manipulation is the canonical manipulation for operating systems that use "\" as directory separators, such as Windows. However, it is also useful for bypassing path traversal protection schemes that only assume that the "/" separator is valid.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-280', N'Improper Handling of Insufficient Permissions or Privileges ', N'Base', N'Draft', N'The application does not handle or incorrectly handles when it has insufficient privileges to access resources or functionality as specified by their permissions. This may cause it to follow unexpected code paths that may leave the application in an invalid state.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-281', N'Improper Preservation of Permissions', N'Base', N'Draft', N'The software does not preserve permissions or incorrectly preserves permissions when copying, restoring, or sharing objects, which can cause them to have less restrictive permissions than intended.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-282', N'Improper Ownership Management', N'Class', N'Draft', N'The software assigns the wrong ownership, or does not properly verify the ownership, of an object or resource.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-283', N'Unverified Ownership', N'Base', N'Draft', N'The software does not properly verify that a critical resource is owned by the proper entity.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-284', N'Improper Access Control', N'Class', N'Incomplete', N'The software does not restrict or incorrectly restricts access to a resource from an unauthorized actor.', N'Access control involves the use of several protection mechanisms such as:
            
               Authentication (proving the identity of an actor)
               Authorization (ensuring that a given actor can access a resource), and
               Accountability (tracking of activities that were performed)
            
            When any mechanism is not applied or otherwise fails, attackers can compromise the security of the software by gaining privileges, reading sensitive information, executing commands, evading detection, etc.
            There are two distinct behaviors that can introduce access control weaknesses:
            
               
                  Specification: incorrect privileges, permissions, ownership, etc. are explicitly specified for either the user or the resource (for example, setting a password file to be world-writable, or giving administrator capabilities to a guest user). This action could be performed by the program or the administrator.
                  Enforcement: the mechanism contains errors that prevent it from properly enforcing the specified access control requirements (e.g., allowing the user to specify their own privileges, or allowing a syntactically-incorrect ACL to produce insecure settings). This problem occurs within the program itself, in that it does not actually enforce the intended security policy that the administrator specifies.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-285', N'Improper Authorization', N'Class', N'Draft', N'The software does not perform or incorrectly performs an authorization check when an actor attempts to access a resource or perform an action.', N'Assuming a user with a given identity, authorization is the process of determining whether that user can access a given resource, based on the user''s privileges and any permissions or other access-control specifications that apply to the resource.
            When access control checks are not applied consistently - or not at all - users are able to access data or perform actions that they should not be allowed to perform. This can lead to a wide range of problems, including information exposures, denial of service, and arbitrary code execution.', N'An access control list (ACL) represents who/what has permissions to a given object. Different operating systems implement (ACLs) in different ways. In UNIX, there are three types of permissions: read, write, and execute. Users are divided into three classes for file access: owner, group owner, and all other users where each class has a separate set of rights. In Windows NT, there are four basic types of permissions for files: "No access", "Read access", "Change access", and "Full control". Windows NT extends the concept of three types of users in UNIX to include a list of users and groups along with their associated permissions. A user can create an object (file) and assign specified permissions to that object.', NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-286', N'Incorrect User Management', N'Class', N'Incomplete', N'The software does not properly manage a user within its environment.', N'Users can be assigned to the wrong group (class) of permissions resulting in unintended access rights to sensitive objects.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-287', N'Improper Authentication', N'Class', N'Draft', N'When an actor claims to have a given identity, the software does not prove or insufficiently proves that the claim is correct.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-288', N'Authentication Bypass Using an Alternate Path or Channel', N'Base', N'Incomplete', N'A product requires authentication, but the product has an alternate path or channel that does not require authentication.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-289', N'Authentication Bypass by Alternate Name', N'Variant', N'Incomplete', N'The software performs authentication based on the name of a resource being accessed, or the name of the actor performing the access, but it does not properly check all possible names for that resource or actor.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-29', N'Path Traversal: ''\..\filename''', N'Variant', N'Incomplete', N'The software uses external input to construct a pathname that should be within a restricted directory, but it does not properly neutralize ''\..\filename'' (leading backslash dot dot) sequences that can resolve to a location that is outside of that directory.', N'This allows attackers to traverse the file system to access files or directories that are outside of the restricted directory.
            This is similar to CWE-25, except using "\" instead of "/". Sometimes a program checks for "..\" at the beginning of the input, so a "\..\" can bypass that check. It is also useful for bypassing path traversal protection schemes that only assume that the "/" separator is valid.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-290', N'Authentication Bypass by Spoofing', N'Base', N'Incomplete', N'This attack-focused weakness is caused by improperly implemented authentication schemes that are subject to spoofing attacks.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-291', N'Reliance on IP Address for Authentication', N'Variant', N'Incomplete', N'The software uses an IP address for authentication.', N'IP addresses can be easily spoofed. Attackers can forge the source IP address of the packets they send, but response packets will return to the forged IP address. To see the response packets, the attacker has to sniff the traffic between the victim machine and the forged IP address. In order to accomplish the required sniffing, attackers typically attempt to locate themselves on the same subnet as the victim machine. Attackers may be able to circumvent this requirement by using source routing, but source routing is disabled across much of the Internet today. In summary, IP address verification can be a useful part of an authentication scheme, but it should not be the single factor required for authentication.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-292', N'DEPRECATED (Duplicate): Trusting Self-reported DNS Name', N'Variant', N'Deprecated', N'This entry has been deprecated because it was a duplicate of CWE-350. All content has been transferred to CWE-350.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-293', N'Using Referer Field for Authentication', N'Variant', N'Draft', N'The referer field in HTTP requests can be easily modified and, as such, is not a valid means of message integrity checking.', NULL, N'The referer field in HTML requests can be simply modified by malicious users, rendering it useless as a means of checking the validity of the request in question.', NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-294', N'Authentication Bypass by Capture-replay', N'Base', N'Incomplete', N'A capture-replay flaw exists when the design of the software makes it possible for a malicious user to sniff network traffic and bypass authentication by replaying it to the server in question to the same effect as the original message (or with minor changes).', N'Capture-replay attacks are common and can be difficult to defeat without cryptography. They are a subset of network injection attacks that rely on observing previously-sent valid commands, then changing them slightly if necessary and resending the same commands to the server.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-295', N'Improper Certificate Validation', N'Base', N'Incomplete', N'The software does not validate, or incorrectly validates, a certificate.', N'When a certificate is invalid or malicious, it might allow an attacker to spoof a trusted entity by using a man-in-the-middle (MITM) attack. The software might connect to a malicious host while believing it is a trusted host, or the software might be deceived into accepting spoofed data that appears to originate from a trusted host.', N'A certificate is a token that associates an identity (principal) to a cryptographic key. Certificates can be used to check if a public key belongs to the assumed owner.', NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-296', N'Improper Following of a Certificate''s Chain of Trust', N'Base', N'Draft', N'The software does not follow, or incorrectly follows, the chain of trust for a certificate back to a trusted root certificate, resulting in incorrect trust of any resource that is associated with that certificate.', N'If a system does not follow the chain of trust of a certificate to a root server, the certificate loses all usefulness as a metric of trust. Essentially, the trust gained from a certificate is derived from a chain of trust -- with a reputable trusted entity at the end of that list. The end user must trust that reputable source, and this reputable source must vouch for the resource in question through the medium of the certificate.
            In some cases, this trust traverses several entities who vouch for one another. The entity trusted by the end user is at one end of this trust chain, while the certificate-wielding resource is at the other end of the chain. If the user receives a certificate at the end of one of these trust chains and then proceeds to check only that the first link in the chain, no real trust has been derived, since the entire chain must be traversed back to a trusted source to verify the certificate.
            There are several ways in which the chain of trust might be broken, including but not limited to:
            
               
                  Any certificate in the chain is self-signed, unless it the root.
                  Not every intermediate certificate is checked, starting from the original certificate all the way up to the root certificate.
                  An intermediate, CA-signed certificate does not have the expected Basic Constraints or other important extensions.
                  The root certificate has been compromised or authorized to the wrong party.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-297', N'Improper Validation of Certificate with Host Mismatch', N'Variant', N'Incomplete', N'The software communicates with a host that provides a certificate, but the software does not properly ensure that the certificate is actually associated with that host.', N'Even if a certificate is well-formed, signed, and follows the chain of trust, it may simply be a valid certificate for a different site than the site that the software is interacting with. If the certificate''s host-specific data is not properly checked - such as the Common Name (CN) in the Subject or the Subject Alternative Name (SAN) extension of an X.509 certificate - it may be possible for a redirection or spoofing attack to allow a malicious host with a valid certificate to provide data, impersonating a trusted host. In order to ensure data integrity, the certificate must be valid and it must pertain to the site that is being accessed.
            Even if the software attempts to check the hostname, it is still possible to incorrectly check the hostname. For example, attackers could create a certificate with a name that begins with a trusted name followed by a NUL byte, which could cause some string-based comparisons to only examine the portion that contains the trusted name.
            This weakness can occur even when the software uses Certificate Pinning, if the software does not verify the hostname at the time a certificate is pinned.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-298', N'Improper Validation of Certificate Expiration', N'Variant', N'Draft', N'A certificate expiration is not validated or is incorrectly validated, so trust may be assigned to certificates that have been abandoned due to age.', N'When the expiration of a certificate is not taken into account, no trust has necessarily been conveyed through it. Therefore, the validity of the certificate cannot be verified and all benefit of the certificate is lost.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-299', N'Improper Check for Certificate Revocation', N'Base', N'Draft', N'The software does not check or incorrectly checks the revocation status of a certificate, which may cause it to use a certificate that has been compromised.', N'An improper check for certificate revocation is a far more serious flaw than related certificate failures. This is because the use of any revoked certificate is almost certainly malicious. The most common reason for certificate revocation is compromise of the system in question, with the result that no legitimate servers will be using a revoked certificate, unless they are sorely out of sync.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-3', N'DEPRECATED: Technology-specific Environment Issues', NULL, N'Deprecated', N'This category has been deprecated. It was originally intended as a "catch-all" for environment issues for technologies that did not have their own CWE, but it introduced unnecessary depth and complexity to the Development View (CWE-699).', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-30', N'Path Traversal: ''\dir\..\filename''', N'Variant', N'Draft', N'The software uses external input to construct a pathname that should be within a restricted directory, but it does not properly neutralize ''\dir\..\filename'' (leading backslash dot dot) sequences that can resolve to a location that is outside of that directory.', N'This allows attackers to traverse the file system to access files or directories that are outside of the restricted directory.
            This is similar to CWE-26, except using "\" instead of "/". The ''\dir\..\filename'' manipulation is useful for bypassing some path traversal protection schemes. Sometimes a program only checks for "..\" at the beginning of the input, so a "\..\" can bypass that check.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-300', N'Channel Accessible by Non-Endpoint (''Man-in-the-Middle'')', N'Class', N'Draft', N'The product does not adequately verify the identity of actors at both ends of a communication channel, or does not adequately ensure the integrity of the channel, in a way that allows the channel to be accessed or influenced by an actor that is not an endpoint.', N'In order to establish secure communication between two parties, it is often important to adequately verify the identity of entities at each end of the communication channel. Inadequate or inconsistent verification may result in insufficient or incorrect identification of either communicating entity. This can have negative consequences such as misplaced trust in the entity at the other end of the channel. An attacker can leverage this by interposing between the communicating entities and masquerading as the original entity. In the absence of sufficient verification of identity, such an attacker can eavesdrop and potentially modify the communication between the original entities.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-301', N'Reflection Attack in an Authentication Protocol', N'Variant', N'Draft', N'Simple authentication protocols are subject to reflection attacks if a malicious user can use the target machine to impersonate a trusted user.', N'A mutual authentication protocol requires each party to respond to a random challenge by the other party by encrypting it with a pre-shared key. Often, however, such protocols employ the same pre-shared key for communication with a number of different entities. A malicious user or an attacker can easily compromise this protocol without possessing the correct key by employing a reflection attack on the protocol.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-302', N'Authentication Bypass by Assumed-Immutable Data', N'Variant', N'Incomplete', N'The authentication scheme or implementation uses key data elements that are assumed to be immutable, but can be controlled or modified by the attacker.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-303', N'Incorrect Implementation of Authentication Algorithm', N'Base', N'Draft', N'The requirements for the software dictate the use of an established authentication algorithm, but the implementation of the algorithm is incorrect.', N'This incorrect implementation may allow authentication to be bypassed.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-304', N'Missing Critical Step in Authentication', N'Base', N'Draft', N'The software implements an authentication technique, but it skips a step that weakens the technique.', N'Authentication techniques should follow the algorithms that define them exactly, otherwise authentication can be bypassed or more easily subjected to brute force attacks.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-305', N'Authentication Bypass by Primary Weakness', N'Base', N'Draft', N'The authentication algorithm is sound, but the implemented mechanism can be bypassed as the result of a separate weakness that is primary to the authentication error.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-306', N'Missing Authentication for Critical Function', N'Base', N'Draft', N'The software does not perform any authentication for functionality that requires a provable user identity or consumes a significant amount of resources.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-307', N'Improper Restriction of Excessive Authentication Attempts', N'Base', N'Draft', N'The software does not implement sufficient measures to prevent multiple failed authentication attempts within in a short time frame, making it more susceptible to brute force attacks.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-308', N'Use of Single-factor Authentication', N'Base', N'Draft', N'The use of single-factor authentication can lead to unnecessary risk of compromise when compared with the benefits of a dual-factor authentication scheme.', N'While the use of multiple authentication schemes is simply piling on more complexity on top of authentication, it is inestimably valuable to have such measures of redundancy. The use of weak, reused, and common passwords is rampant on the internet. Without the added protection of multiple authentication schemes, a single mistake can result in the compromise of an account. For this reason, if multiple schemes are possible and also easy to use, they should be implemented and required.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-309', N'Use of Password System for Primary Authentication', N'Base', N'Draft', N'The use of password systems as the primary means of authentication may be subject to several flaws or shortcomings, each reducing the effectiveness of the mechanism.', NULL, N'Password systems are the simplest and most ubiquitous authentication mechanisms. However, they are subject to such well known attacks,and such frequent compromise that their use in the most simple implementation is not practical.', NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-31', N'Path Traversal: ''dir\..\..\filename''', N'Variant', N'Draft', N'The software uses external input to construct a pathname that should be within a restricted directory, but it does not properly neutralize ''dir\..\..\filename'' (multiple internal backslash dot dot) sequences that can resolve to a location that is outside of that directory.', N'This allows attackers to traverse the file system to access files or directories that are outside of the restricted directory.
            The ''dir\..\..\filename'' manipulation is useful for bypassing some path traversal protection schemes. Sometimes a program only removes one "..\" sequence, so multiple "..\" can bypass that check. Alternately, this manipulation could be used to bypass a check for "..\" at the beginning of the pathname, moving up more than one directory level.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-310', N'Cryptographic Issues', NULL, N'Draft', N'Weaknesses in this category are related to the use of cryptography.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-311', N'Missing Encryption of Sensitive Data', N'Class', N'Draft', N'The software does not encrypt sensitive or critical information before storage or transmission.', N'The lack of proper data encryption passes up the guarantees of confidentiality, integrity, and accountability that properly implemented encryption conveys.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-312', N'Cleartext Storage of Sensitive Information', N'Base', N'Draft', N'The application stores sensitive information in cleartext within a resource that might be accessible to another control sphere.', N'Because the information is stored in cleartext, attackers could potentially read it. Even if the information is encoded in a way that is not human-readable, certain techniques could determine which encoding is being used, then decode the information.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-313', N'Cleartext Storage in a File or on Disk', N'Variant', N'Draft', N'The application stores sensitive information in cleartext in a file, or on disk.', N'The sensitive information could be read by attackers with access to the file, or with physical or administrator access to the raw disk. Even if the information is encoded in a way that is not human-readable, certain techniques could determine which encoding is being used, then decode the information.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-314', N'Cleartext Storage in the Registry', N'Variant', N'Draft', N'The application stores sensitive information in cleartext in the registry.', N'Attackers can read the information by accessing the registry key. Even if the information is encoded in a way that is not human-readable, certain techniques could determine which encoding is being used, then decode the information.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-315', N'Cleartext Storage of Sensitive Information in a Cookie', N'Variant', N'Draft', N'The application stores sensitive information in cleartext in a cookie.', N'Attackers can use widely-available tools to view the cookie and read the sensitive information. Even if the information is encoded in a way that is not human-readable, certain techniques could determine which encoding is being used, then decode the information.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-316', N'Cleartext Storage of Sensitive Information in Memory', N'Variant', N'Draft', N'The application stores sensitive information in cleartext in memory.', N'The sensitive memory might be saved to disk, stored in a core dump, or remain uncleared if the application crashes, or if the programmer does not properly clear the memory before freeing it.
            It could be argued that such problems are usually only exploitable by those with administrator privileges. However, swapping could cause the memory to be written to disk and leave it accessible to physical attack afterwards. Core dump files might have insecure permissions or be stored in archive files that are accessible to untrusted people. Or, uncleared sensitive memory might be inadvertently exposed to attackers due to another weakness.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-317', N'Cleartext Storage of Sensitive Information in GUI', N'Variant', N'Draft', N'The application stores sensitive information in cleartext within the GUI.', N'An attacker can often obtain data from a GUI, even if hidden, by using an API to directly access GUI objects such as windows and menus. Even if the information is encoded in a way that is not human-readable, certain techniques could determine which encoding is being used, then decode the information.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-318', N'Cleartext Storage of Sensitive Information in Executable', N'Variant', N'Draft', N'The application stores sensitive information in cleartext in an executable.', N'Attackers can reverse engineer binary code to obtain secret data. This is especially easy when the cleartext is plain ASCII. Even if the information is encoded in a way that is not human-readable, certain techniques could determine which encoding is being used, then decode the information.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-319', N'Cleartext Transmission of Sensitive Information', N'Base', N'Draft', N'The software transmits sensitive or security-critical data in cleartext in a communication channel that can be sniffed by unauthorized actors.', N'Many communication channels can be "sniffed" by attackers during data transmission. For example, network traffic can often be sniffed by any attacker who has access to a network interface. This significantly lowers the difficulty of exploitation by attackers.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-32', N'Path Traversal: ''...'' (Triple Dot)', N'Variant', N'Incomplete', N'The software uses external input to construct a pathname that should be within a restricted directory, but it does not properly neutralize ''...'' (triple dot) sequences that can resolve to a location that is outside of that directory.', N'This allows attackers to traverse the file system to access files or directories that are outside of the restricted directory.
            The ''...'' manipulation is useful for bypassing some path traversal protection schemes. On some Windows systems, it is equivalent to "..\.." and might bypass checks that assume only two dots are valid. Incomplete filtering, such as removal of "./" sequences, can ultimately produce valid ".." sequences due to a collapse into unsafe value (CWE-182).', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-320', N'Key Management Errors', NULL, N'Draft', N'Weaknesses in this category are related to errors in the management of cryptographic keys.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-321', N'Use of Hard-coded Cryptographic Key', N'Base', N'Draft', N'The use of a hard-coded cryptographic key significantly increases the possibility that encrypted data may be recovered.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-322', N'Key Exchange without Entity Authentication', N'Base', N'Draft', N'The software performs a key exchange with an actor without verifying the identity of that actor.', N'Performing a key exchange will preserve the integrity of the information sent between two entities, but this will not guarantee that the entities are who they claim they are. This may enable a set of "man-in-the-middle" attacks. Typically, this involves a victim client that contacts a malicious server that is impersonating a trusted server. If the client skips authentication or ignores an authentication failure, the malicious server may request authentication information from the user. The malicious server can then use this authentication information to log in to the trusted server using the victim''s credentials, sniff traffic between the victim and trusted server, etc.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-323', N'Reusing a Nonce, Key Pair in Encryption', N'Variant', N'Incomplete', N'Nonces should be used for the present occasion and only once.', NULL, N'Nonces are often bundled with a key in a communication exchange to produce a new session key for each exchange.', NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-324', N'Use of a Key Past its Expiration Date', N'Base', N'Draft', N'The product uses a cryptographic key or password past its expiration date, which diminishes its safety significantly by increasing the timing window for cracking attacks against that key.', N'While the expiration of keys does not necessarily ensure that they are compromised, it is a significant concern that keys which remain in use for prolonged periods of time have a decreasing probability of integrity. For this reason, it is important to replace keys within a period of time proportional to their strength.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-325', N'Missing Required Cryptographic Step', N'Base', N'Incomplete', N'The software does not implement a required step in a cryptographic algorithm, resulting in weaker encryption than advertised by that algorithm.', N'Cryptographic implementations should follow the algorithms that define them exactly, otherwise encryption can be weaker than expected.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-326', N'Inadequate Encryption Strength', N'Class', N'Draft', N'The software stores or transmits sensitive data using an encryption scheme that is theoretically sound, but is not strong enough for the level of protection required.', N'A weak encryption scheme can be subjected to brute force attacks that have a reasonable chance of succeeding using current attack methods and resources.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-327', N'Use of a Broken or Risky Cryptographic Algorithm', N'Class', N'Draft', N'The use of a broken or risky cryptographic algorithm is an unnecessary risk that may result in the exposure of sensitive information.', N'The use of a non-standard algorithm is dangerous because a determined attacker may be able to break the algorithm and compromise whatever data has been protected. Well-known techniques may exist to break the algorithm.', N'Cryptographic algorithms are the methods by which data is scrambled. There are a small number of well-understood and heavily studied algorithms that should be used by most applications. It is quite difficult to produce a secure algorithm, and even high profile algorithms by accomplished cryptographic experts have been broken.
               Since the state of cryptography advances so rapidly, it is common for an algorithm to be considered "unsafe" even if it was once thought to be strong. This can happen when new attacks against the algorithm are discovered, or if computing power increases so much that the cryptographic algorithm no longer provides the amount of protection that was originally thought.', NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-328', N'Reversible One-Way Hash', N'Base', N'Draft', N'The product uses a hashing algorithm that produces a hash value that can be used to determine the original input, or to find an input that can produce the same hash, more efficiently than brute force techniques.', N'This weakness is especially dangerous when the hash is used in security algorithms that require the one-way property to hold. For example, if an authentication system takes an incoming password and generates a hash, then compares the hash to another hash that it has stored in its authentication database, then the ability to create a collision could allow an attacker to provide an alternate password that produces the same target hash, bypassing authentication.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-329', N'Not Using a Random IV with CBC Mode', N'Variant', N'Draft', N'Not using a random initialization Vector (IV) with Cipher Block Chaining (CBC) Mode causes algorithms to be susceptible to dictionary attacks.', NULL, N'CBC is the most commonly used mode of operation for a block cipher. It solves electronic code book''s dictionary problems by XORing the ciphertext with plaintext. If it used to encrypt multiple data streams, dictionary attacks are possible, provided that the streams have a common beginning sequence.', NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-33', N'Path Traversal: ''....'' (Multiple Dot)', N'Variant', N'Incomplete', N'The software uses external input to construct a pathname that should be within a restricted directory, but it does not properly neutralize ''....'' (multiple dot) sequences that can resolve to a location that is outside of that directory.', N'This allows attackers to traverse the file system to access files or directories that are outside of the restricted directory.
            The ''....'' manipulation is useful for bypassing some path traversal protection schemes. On some Windows systems, it is equivalent to "..\..\.." and might bypass checks that assume only two dots are valid. Incomplete filtering, such as removal of "./" sequences, can ultimately produce valid ".." sequences due to a collapse into unsafe value (CWE-182).', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-330', N'Use of Insufficiently Random Values', N'Class', N'Usable', N'The software may use insufficiently random numbers or values in a security context that depends on unpredictable numbers.', N'When software generates predictable values in a context requiring unpredictability, it may be possible for an attacker to guess the next value that will be generated, and use this guess to impersonate another user or access sensitive information.', N'Computers are deterministic machines, and as such are unable to produce true randomness. Pseudo-Random Number Generators (PRNGs) approximate randomness algorithmically, starting with a seed from which subsequent values are calculated. There are two types of PRNGs: statistical and cryptographic. Statistical PRNGs provide useful statistical properties, but their output is highly predictable and forms an easy to reproduce numeric stream that is unsuitable for use in cases where security depends on generated values being unpredictable. Cryptographic PRNGs address this problem by generating output that is more difficult to predict. For a value to be cryptographically secure, it must be impossible or highly improbable for an attacker to distinguish between it and a truly random value.', NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-331', N'Insufficient Entropy', N'Base', N'Draft', N'The software uses an algorithm or scheme that produces insufficient entropy, leaving patterns or clusters of values that are more likely to occur than others.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-332', N'Insufficient Entropy in PRNG', N'Variant', N'Draft', N'The lack of entropy available for, or used by, a Pseudo-Random Number Generator (PRNG) can be a stability and security threat.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-333', N'Improper Handling of Insufficient Entropy in TRNG', N'Variant', N'Draft', N'True random number generators (TRNG) generally have a limited source of entropy and therefore can fail or block.', N'The rate at which true random numbers can be generated is limited. It is important that one uses them only when they are needed for security.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-334', N'Small Space of Random Values', N'Base', N'Draft', N'The number of possible random values is smaller than needed by the product, making it more susceptible to brute force attacks.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-335', N'Incorrect Usage of Seeds in Pseudo-Random Number Generator (PRNG)', N'Base', N'Draft', N'The software uses a Pseudo-Random Number Generator (PRNG) that does not correctly manage seeds.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-336', N'Same Seed in Pseudo-Random Number Generator (PRNG)', N'Variant', N'Draft', N'A Pseudo-Random Number Generator (PRNG) uses the same seed each time the product is initialized.', N'If an attacker can guess (or knows) the seed, then the attacker may be able to determine the random numbers that will be produced from the PRNG.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-337', N'Predictable Seed in Pseudo-Random Number Generator (PRNG)', N'Variant', N'Draft', N'A Pseudo-Random Number Generator (PRNG) is initialized from a predictable seed, such as the process ID or system time.', N'The use of predictable seeds significantly reduces the number of possible seeds that an attacker would need to test in order to predict which random numnbers will be generated by the PRNG.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-338', N'Use of Cryptographically Weak Pseudo-Random Number Generator (PRNG)', N'Base', N'Draft', N'The product uses a Pseudo-Random Number Generator (PRNG) in a security context, but the PRNG''s algorithm is not cryptographically strong.', N'When a non-cryptographic PRNG is used in a cryptographic context, it can expose the cryptography to certain types of attacks.
            Often a pseudo-random number generator (PRNG) is not designed for cryptography. Sometimes a mediocre source of randomness is sufficient or preferable for algorithms that use random numbers. Weak generators generally take less processing power and/or do not use the precious, finite, entropy sources on a system. While such PRNGs might have very useful features, these same features could be used to break the cryptography.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-339', N'Small Seed Space in PRNG', N'Variant', N'Draft', N'A PRNG uses a relatively small space of seeds.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-34', N'Path Traversal: ''....//''', N'Variant', N'Incomplete', N'The software uses external input to construct a pathname that should be within a restricted directory, but it does not properly neutralize ''....//'' (doubled dot dot slash) sequences that can resolve to a location that is outside of that directory.', N'This allows attackers to traverse the file system to access files or directories that are outside of the restricted directory.
            The ''....//'' manipulation is useful for bypassing some path traversal protection schemes. If "../" is filtered in a sequential fashion, as done by some regular expression engines, then "....//" can collapse into the "../" unsafe value (CWE-182). It could also be useful when ".." is removed, if the operating system treats "//" and "/" as equivalent.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-340', N'Predictability Problems', N'Class', N'Incomplete', N'Weaknesses in this category are related to schemes that generate numbers or identifiers that are more predictable than required by the application.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-341', N'Predictable from Observable State', N'Base', N'Draft', N'A number or object is predictable based on observations that the attacker can make about the state of the system or network, such as time, process ID, etc.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-342', N'Predictable Exact Value from Previous Values', N'Base', N'Draft', N'An exact value or random number can be precisely predicted by observing previous values.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-343', N'Predictable Value Range from Previous Values', N'Base', N'Draft', N'The software''s random number generator produces a series of values which, when observed, can be used to infer a relatively small range of possibilities for the next value that could be generated.', N'The output of a random number generator should not be predictable based on observations of previous values. In some cases, an attacker cannot predict the exact value that will be produced next, but can narrow down the possibilities significantly. This reduces the amount of effort to perform a brute force attack. For example, suppose the product generates random numbers between 1 and 100, but it always produces a larger value until it reaches 100. If the generator produces an 80, then the attacker knows that the next value will be somewhere between 81 and 100. Instead of 100 possibilities, the attacker only needs to consider 20.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-344', N'Use of Invariant Value in Dynamically Changing Context', N'Base', N'Draft', N'The product uses a constant value, name, or reference, but this value can (or should) vary across different environments.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-345', N'Insufficient Verification of Data Authenticity', N'Class', N'Draft', N'The software does not sufficiently verify the origin or authenticity of data, in a way that causes it to accept invalid data.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-346', N'Origin Validation Error', N'Base', N'Draft', N'The software does not properly verify that the source of data or communication is valid.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-347', N'Improper Verification of Cryptographic Signature', N'Base', N'Draft', N'The software does not verify, or incorrectly verifies, the cryptographic signature for data.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-348', N'Use of Less Trusted Source', N'Base', N'Draft', N'The software has two different sources of the same data or information, but it uses the source that has less support for verification, is less trusted, or is less resistant to attack.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-349', N'Acceptance of Extraneous Untrusted Data With Trusted Data', N'Base', N'Draft', N'The software, when processing trusted data, accepts any untrusted data that is also included with the trusted data, treating the untrusted data as if it were trusted.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-35', N'Path Traversal: ''.../...//''', N'Variant', N'Incomplete', N'The software uses external input to construct a pathname that should be within a restricted directory, but it does not properly neutralize ''.../...//'' (doubled triple dot slash) sequences that can resolve to a location that is outside of that directory.', N'This allows attackers to traverse the file system to access files or directories that are outside of the restricted directory.
            The ''.../...//'' manipulation is useful for bypassing some path traversal protection schemes. If "../" is filtered in a sequential fashion, as done by some regular expression engines, then ".../...//" can collapse into the "../" unsafe value (CWE-182). Removing the first "../" yields "....//"; the second removal yields "../". Depending on the algorithm, the software could be susceptible to CWE-34 but not CWE-35, or vice versa.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-350', N'Reliance on Reverse DNS Resolution for a Security-Critical Action', N'Variant', N'Draft', N'The software performs reverse DNS resolution on an IP address to obtain the hostname and make a security decision, but it does not properly ensure that the IP address is truly associated with the hostname.', N'Since DNS names can be easily spoofed or misreported, and it may be difficult for the software to detect if a trusted DNS server has been compromised, DNS names do not constitute a valid authentication mechanism.
            When the software performs a reverse DNS resolution for an IP address, if an attacker controls the server for that IP address, then the attacker can cause the server to return an arbitrary hostname. As a result, the attacker may be able to bypass authentication, cause the wrong hostname to be recorded in log files to hide activities, or perform other attacks.
            Attackers can spoof DNS names by either (1) compromising a DNS server and modifying its records (sometimes called DNS cache poisoning), or (2) having legitimate control over a DNS server associated with their IP address.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-351', N'Insufficient Type Distinction', N'Base', N'Draft', N'The software does not properly distinguish between different types of elements in a way that leads to insecure behavior.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-352', N'Cross-Site Request Forgery (CSRF)', N'Compound', N'Draft', N'The web application does not, or can not, sufficiently verify whether a well-formed, valid, consistent request was intentionally provided by the user who submitted the request.', N'When a web server is designed to receive a request from a client without any mechanism for verifying that it was intentionally sent, then it might be possible for an attacker to trick a client into making an unintentional request to the web server which will be treated as an authentic request. This can be done via a URL, image load, XMLHttpRequest, etc. and can result in exposure of data or unintended code execution.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-353', N'Missing Support for Integrity Check', N'Base', N'Draft', N'The software uses a transmission protocol that does not include a mechanism for verifying the integrity of the data during transmission, such as a checksum.', N'If integrity check values or "checksums" are omitted from a protocol, there is no way of determining if data has been corrupted in transmission. The lack of checksum functionality in a protocol removes the first application-level check of data that can be used. The end-to-end philosophy of checks states that integrity checks should be performed at the lowest level that they can be completely implemented. Excluding further sanity checks and input validation performed by applications, the protocol''s checksum is the most important level of checksum, since it can be performed more completely than at any previous level and takes into account entire messages, as opposed to single packets.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-354', N'Improper Validation of Integrity Check Value', N'Base', N'Draft', N'The software does not validate or incorrectly validates the integrity check values or "checksums" of a message. This may prevent it from detecting if the data has been modified or corrupted in transmission.', N'Improper validation of checksums before use results in an unnecessary risk that can easily be mitigated. The protocol specification describes the algorithm used for calculating the checksum. It is then a simple matter of implementing the calculation and verifying that the calculated checksum and the received checksum match. Improper verification of the calculated checksum and the received checksum can lead to far greater consequences.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-355', N'User Interface Security Issues', NULL, N'Draft', N'Weaknesses in this category are related to or introduced in the User Interface (UI).', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-356', N'Product UI does not Warn User of Unsafe Actions', N'Base', N'Incomplete', N'The software''s user interface does not warn the user before undertaking an unsafe action on behalf of that user. This makes it easier for attackers to trick users into inflicting damage to their system.', N'Software systems should warn users that a potentially dangerous action may occur if the user proceeds. For example, if the user downloads a file from an unknown source and attempts to execute the file on their machine, then the application''s GUI can indicate that the file is unsafe.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-357', N'Insufficient UI Warning of Dangerous Operations', N'Base', N'Draft', N'The user interface provides a warning to a user regarding dangerous or sensitive operations, but the warning is not noticeable enough to warrant attention.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-358', N'Improperly Implemented Security Check for Standard', N'Base', N'Draft', N'The software does not implement or incorrectly implements one or more security-relevant checks as specified by the design of a standardized algorithm, protocol, or technique.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-359', N'Exposure of Private Information (''Privacy Violation'')', N'Class', N'Incomplete', N'The software does not properly prevent private data (such as credit card numbers) from being accessed by actors who either (1) are not explicitly authorized to access the data or (2) do not have the implicit consent of the people to which the data is related.', N'Mishandling private information, such as customer passwords or Social Security numbers, can compromise user privacy and is often illegal. An exposure of private information does not necessarily prevent the software from working properly, and in fact it might be intended by the developer, but it can still be undesirable (or explicitly prohibited by law) for the people who are associated with this private information.
            Privacy violations may occur when:
            
               
                  Private user information enters the program.
                  The data is written to an external location, such as the console, file system, or network.
               
            
            Private data can enter a program in a variety of ways:
            
               
                  Directly from the user in the form of a password or personal information
                  Accessed from a database or other data store by the application
                  Indirectly from a partner or other third party
               
            
            Some types of private information include:
            
               
                  Government identifiers, such as Social Security Numbers
                  Contact information, such as home addresses and telephone numbers
                  Geographic location - where the user is (or was)
                  Employment history
                  Financial data - such as credit card numbers, salary, bank accounts, and debts
                  Pictures, video, or audio
                  Behavioral patterns - such as web surfing history, when certain activities are performed, etc.
                  Relationships (and types of relationships) with others - family, friends, contacts, etc.
                  Communications - e-mail addresses, private e-mail messages, SMS text messages, chat logs, etc.
                  Health - medical conditions, insurance status, prescription records
                  Credentials, such as passwords, which can be used to access other information.
               
            
            Some of this information may be characterized as PII (Personally Identifiable Information), Protected Health Information (PHI), etc. Categories of private information may overlap or vary based on the intended usage or the policies and practices of a particular industry.
            Depending on its location, the type of business it conducts, and the nature of any private data it handles, an organization may be required to comply with one or more of the following federal and state regulations: - Safe Harbor Privacy Framework [REF-340] - Gramm-Leach Bliley Act (GLBA) [REF-341] - Health Insurance Portability and Accountability Act (HIPAA) [REF-342] - California SB-1386 [REF-343].
            Sometimes data that is not labeled as private can have a privacy implication in a different context. For example, student identification numbers are usually not considered private because there is no explicit and publicly-available mapping to an individual student''s personal information. However, if a school generates identification numbers based on student social security numbers, then the identification numbers should be considered private.
            Security and privacy concerns often seem to compete with each other. From a security perspective, all important operations should be recorded so that any anomalous activity can later be identified. However, when private data is involved, this practice can in fact create risk. Although there are many ways in which private data can be handled unsafely, a common risk stems from misplaced trust. Programmers often trust the operating environment in which a program runs, and therefore believe that it is acceptable store private information on the file system, in the registry, or in other locally-controlled resources. However, even if access to certain resources is restricted, this does not guarantee that the individuals who do have access can be trusted.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-36', N'Absolute Path Traversal', N'Base', N'Draft', N'The software uses external input to construct a pathname that should be within a restricted directory, but it does not properly neutralize absolute path sequences such as "/abs/path" that can resolve to a location that is outside of that directory.', N'This allows attackers to traverse the file system to access files or directories that are outside of the restricted directory.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-360', N'Trust of System Event Data', N'Base', N'Incomplete', N'Security based on event locations are insecure and can be spoofed.', N'Events are a messaging system which may provide control data to programs listening for events. Events often do not have any type of authentication framework to allow them to be verified from a trusted source. Any application, in Windows, on a given desktop can send a message to any window on the same desktop. There is no authentication framework for these messages. Therefore, any message can be used to manipulate any process on the desktop if the process does not check the validity and safeness of those messages.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-361', N'7PK - Time and State', NULL, N'Incomplete', N'This category represents one of the phyla in the Seven Pernicious Kingdoms vulnerability classification. It includes weaknesses related to the improper management of time and state in an environment that supports simultaneous or near-simultaneous computation by multiple systems, processes, or threads. According to the authors of the Seven Pernicious Kingdoms, "Distributed computation is about time and state. That is, in order for more than one component to communicate, state must be shared, and all that takes time. Most programmers anthropomorphize their work. They think about one thread of control carrying out the entire program in the same way they would if they had to do the job themselves. Modern computers, however, switch between tasks very quickly, and in multi-core, multi-CPU, or distributed systems, two events may take place at exactly the same time. Defects rush to fill the gap between the programmer''s model of how a program executes and what happens in reality. These defects are related to unexpected interactions between threads, processes, time, and information. These interactions happen through shared state: semaphores, variables, the file system, and, basically, anything that can store information."', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-362', N'Concurrent Execution using Shared Resource with Improper Synchronization (''Race Condition'')', N'Class', N'Draft', N'The program contains a code sequence that can run concurrently with other code, and the code sequence requires temporary, exclusive access to a shared resource, but a timing window exists in which the shared resource can be modified by another code sequence that is operating concurrently.', N'This can have security implications when the expected synchronization is in security-critical code, such as recording whether a user is authenticated or modifying important state information that should not be influenced by an outsider.
            A race condition occurs within concurrent environments, and is effectively a property of a code sequence. Depending on the context, a code sequence may be in the form of a function call, a small number of instructions, a series of program invocations, etc.
            A race condition violates these properties, which are closely related:
            
               
                  Exclusivity - the code sequence is given exclusive access to the shared resource, i.e., no other code sequence can modify properties of the shared resource before the original sequence has completed execution.
                  Atomicity - the code sequence is behaviorally atomic, i.e., no other thread or process can concurrently execute the same sequence of instructions (or a subset) against the same resource.
               
            
            A race condition exists when an "interfering code sequence" can still access the shared resource, violating exclusivity. Programmers may assume that certain code sequences execute too quickly to be affected by an interfering code sequence; when they are not, this violates atomicity. For example, the single "x++" statement may appear atomic at the code layer, but it is actually non-atomic at the instruction layer, since it involves a read (the original value of x), followed by a computation (x+1), followed by a write (save the result to x).
            The interfering code sequence could be "trusted" or "untrusted." A trusted interfering code sequence occurs within the program; it cannot be modified by the attacker, and it can only be invoked indirectly. An untrusted interfering code sequence can be authored directly by the attacker, and typically it is external to the vulnerable program.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-363', N'Race Condition Enabling Link Following', N'Base', N'Draft', N'The software checks the status of a file or directory before accessing it, which produces a race condition in which the file can be replaced with a link before the access is performed, causing the software to access the wrong file.', N'While developers might expect that there is a very narrow time window between the time of check and time of use, there is still a race condition. An attacker could cause the software to slow down (e.g. with memory consumption), causing the time window to become larger. Alternately, in some situations, the attacker could win the race by performing a large number of attacks.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-364', N'Signal Handler Race Condition', N'Base', N'Incomplete', N'The software uses a signal handler that introduces a race condition.', N'Race conditions frequently occur in signal handlers, since signal handlers support asynchronous actions. These race conditions have a variety of root causes and symptoms. Attackers may be able to exploit a signal handler race condition to cause the software state to be corrupted, possibly leading to a denial of service or even code execution.
            These issues occur when non-reentrant functions, or state-sensitive actions occur in the signal handler, where they may be called at any time. These behaviors can violate assumptions being made by the "regular" code that is interrupted, or by other signal handlers that may also be invoked. If these functions are called at an inopportune moment - such as while a non-reentrant function is already running - memory corruption could occur that may be exploitable for code execution. Another signal race condition commonly found occurs when free is called within a signal handler, resulting in a double free and therefore a write-what-where condition. Even if a given pointer is set to NULL after it has been freed, a race condition still exists between the time the memory was freed and the pointer was set to NULL. This is especially problematic if the same signal handler has been set for more than one signal -- since it means that the signal handler itself may be reentered.
            There are several known behaviors related to signal handlers that have received the label of "signal handler race condition":
            
               
                  Shared state (e.g. global data or static variables) that are accessible to both a signal handler and "regular" code
                  Shared state between a signal handler and other signal handlers
                  Use of non-reentrant functionality within a signal handler - which generally implies that shared state is being used. For example, malloc() and free() are non-reentrant because they may use global or static data structures for managing memory, and they are indirectly used by innocent-seeming functions such as syslog(); these functions could be exploited for memory corruption and, possibly, code execution.
                  Association of the same signal handler function with multiple signals - which might imply shared state, since the same code and resources are accessed. For example, this can be a source of double-free and use-after-free weaknesses.
                  Use of setjmp and longjmp, or other mechanisms that prevent a signal handler from returning control back to the original functionality
                  While not technically a race condition, some signal handlers are designed to be called at most once, and being called more than once can introduce security problems, even when there are not any concurrent calls to the signal handler. This can be a source of double-free and use-after-free weaknesses.
               
            
            Signal handler vulnerabilities are often classified based on the absence of a specific protection mechanism, although this style of classification is discouraged in CWE because programmers often have a choice of several different mechanisms for addressing the weakness. Such protection mechanisms may preserve exclusivity of access to the shared resource, and behavioral atomicity for the relevant code:
            
               
                  Avoiding shared state
                  Using synchronization in the signal handler
                  Using synchronization in the regular code
                  Disabling or masking other signals, which provides atomicity (which effectively ensures exclusivity)', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-365', N'Race Condition in Switch', N'Base', N'Draft', N'The code contains a switch statement in which the switched variable can be modified while the switch is still executing, resulting in unexpected behavior.', N'This issue is particularly important in the case of switch statements that involve fall-through style case statements - ie., those which do not end with break. If the variable being tested by the switch changes in the course of execution, this could change the intended logic of the switch so much that it places the process in a contradictory state and in some cases could even result in memory corruption.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-366', N'Race Condition within a Thread', N'Base', N'Draft', N'If two threads of execution use a resource simultaneously, there exists the possibility that resources may be used while invalid, in turn making the state of execution undefined.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-367', N'Time-of-check Time-of-use (TOCTOU) Race Condition', N'Base', N'Incomplete', N'The software checks the state of a resource before using that resource, but the resource''s state can change between the check and the use in a way that invalidates the results of the check. This can cause the software to perform invalid actions when the resource is in an unexpected state.', N'This weakness can be security-relevant when an attacker can influence the state of the resource between check and use. This can happen with shared resources such as files, memory, or even variables in multithreaded programs.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-368', N'Context Switching Race Condition', N'Base', N'Draft', N'A product performs a series of non-atomic actions to switch between contexts that cross privilege or other security boundaries, but a race condition allows an attacker to modify or misrepresent the product''s behavior during the switch.', N'This is commonly seen in web browser vulnerabilities in which the attacker can perform certain actions while the browser is transitioning from a trusted to an untrusted domain, or vice versa, and the browser performs the actions on one domain using the trust level and resources of the other domain.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-369', N'Divide By Zero', N'Base', N'Draft', N'The product divides a value by zero.', N'This weakness typically occurs when an unexpected value is provided to the product, or if an error occurs that is not properly detected. It frequently occurs in calculations involving physical dimensions such as size, length, width, and height.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-37', N'Path Traversal: ''/absolute/pathname/here''', N'Variant', N'Draft', N'A software system that accepts input in the form of a slash absolute path (''/absolute/pathname/here'') without appropriate validation can allow an attacker to traverse the file system to unintended locations or access arbitrary files.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-370', N'Missing Check for Certificate Revocation after Initial Check', N'Variant', N'Draft', N'The software does not check the revocation status of a certificate after its initial revocation check, which can cause the software to perform privileged actions even after the certificate is revoked at a later time.', N'If the revocation status of a certificate is not checked before each action that requires privileges, the system may be subject to a race condition. If a certificate is revoked after the initial check, all subsequent actions taken with the owner of the revoked certificate will lose all benefits guaranteed by the certificate. In fact, it is almost certain that the use of a revoked certificate indicates malicious activity.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-371', N'State Issues', NULL, N'Draft', N'Weaknesses in this category are related to improper management of system state.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-372', N'Incomplete Internal State Distinction', N'Base', N'Draft', N'The software does not properly determine which state it is in, causing it to assume it is in state X when in fact it is in state Y, causing it to perform incorrect operations in a security-relevant manner.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-373', N'DEPRECATED: State Synchronization Error', N'Base', N'Deprecated', N'This entry was deprecated because it overlapped the same concepts as race condition (CWE-362) and Improper Synchronization (CWE-662).', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-374', N'Passing Mutable Objects to an Untrusted Method', N'Base', N'Draft', N'The program sends non-cloned mutable data as an argument to a method or function.', N'The function or method that has been called can alter or delete the mutable data. This could violate assumptions that the calling function has made about its state. In situations where unknown code is called with references to mutable data, this external code could make changes to the data sent. If this data was not previously cloned, the modified data might not be valid in the context of execution.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-375', N'Returning a Mutable Object to an Untrusted Caller', N'Base', N'Draft', N'Sending non-cloned mutable data as a return value may result in that data being altered or deleted by the calling function.', N'In situations where functions return references to mutable data, it is possible that the external code which called the function may make changes to the data sent. If this data was not previously cloned, the class will then be using modified data which may violate assumptions about its internal state.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-376', N'Temporary File Issues', NULL, N'Draft', N'Weaknesses in this category are related to improper handling of temporary files.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-377', N'Insecure Temporary File', N'Base', N'Incomplete', N'Creating and using insecure temporary files can leave application and system data vulnerable to attack.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-378', N'Creation of Temporary File With Insecure Permissions', N'Base', N'Draft', N'Opening temporary files without appropriate measures or controls can leave the file, its contents and any function that it impacts vulnerable to attack.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-379', N'Creation of Temporary File in Directory with Incorrect Permissions', N'Variant', N'Incomplete', N'The software creates a temporary file in a directory whose permissions allow unintended actors to determine the file''s existence or otherwise access that file.', N'On some operating systems, the fact that the temporary file exists may be apparent to any user with sufficient privileges to access that directory. Since the file is visible, the application that is using the temporary file could be known. If one has access to list the processes on the system, the attacker has gained information about what the user is doing at that time. By correlating this with the applications the user is running, an attacker could potentially discover what a user''s actions are. From this, higher levels of security could be breached.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-38', N'Path Traversal: ''\absolute\pathname\here''', N'Variant', N'Draft', N'A software system that accepts input in the form of a backslash absolute path (''\absolute\pathname\here'') without appropriate validation can allow an attacker to traverse the file system to unintended locations or access arbitrary files.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-380', N'Technology-Specific Time and State Issues', NULL, N'Draft', N'Weaknesses in this category are related to improper handling of time or state within particular technologies.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-381', N'J2EE Time and State Issues', NULL, N'Draft', N'Weaknesses in this category are related to improper handling of time or state within J2EE.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-382', N'J2EE Bad Practices: Use of System.exit()', N'Variant', N'Draft', N'A J2EE application uses System.exit(), which also shuts down its container.', N'It is never a good idea for a web application to attempt to shut down the application container. Access to a function that can shut down the application is an avenue for Denial of Service (DoS) attacks.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-383', N'J2EE Bad Practices: Direct Use of Threads', N'Variant', N'Draft', N'Thread management in a Web application is forbidden in some circumstances and is always highly error prone.', N'Thread management in a web application is forbidden by the J2EE standard in some circumstances and is always highly error prone. Managing threads is difficult and is likely to interfere in unpredictable ways with the behavior of the application container. Even without interfering with the container, thread management usually leads to bugs that are hard to detect and diagnose like deadlock, race conditions, and other synchronization errors.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-384', N'Session Fixation', N'Compound', N'Incomplete', N'Authenticating a user, or otherwise establishing a new user session, without invalidating any existing session identifier gives an attacker the opportunity to steal authenticated sessions.', N'Such a scenario is commonly observed when:
            
               1. A web application authenticates a user without first invalidating the existing session, thereby continuing to use the session already associated with the user.
               2. An attacker is able to force a known session identifier on a user so that, once the user authenticates, the attacker has access to the authenticated session.
               3. The application or container uses predictable session identifiers. In the generic exploit of session fixation vulnerabilities, an attacker creates a new session on a web application and records the associated session identifier. The attacker then causes the victim to associate, and possibly authenticate, against the server using that session identifier, giving the attacker access to the user''s account through the active session.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-385', N'Covert Timing Channel', N'Base', N'Incomplete', N'Covert timing channels convey information by modulating some aspect of system behavior over time, so that the program receiving the information can observe system behavior and infer protected information.', N'In some instances, knowing when data is transmitted between parties can provide a malicious user with privileged information. Also, externally monitoring the timing of operations can potentially reveal sensitive data. For example, a cryptographic operation can expose its internal state if the time it takes to perform the operation varies, based on the state.
            Covert channels are frequently classified as either storage or timing channels. Some examples of covert timing channels are the system''s paging rate, the time a certain transaction requires to execute, and the time it takes to gain access to a shared bus.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-386', N'Symbolic Name not Mapping to Correct Object', N'Base', N'Draft', N'A constant symbolic reference to an object is used, even though the reference can resolve to a different object over time.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-387', N'Signal Errors', NULL, N'Incomplete', N'Weaknesses in this category are related to the improper handling of signals.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-388', N'7PK - Errors', NULL, N'Draft', N'This category represents one of the phyla in the Seven Pernicious Kingdoms vulnerability classification. It includes weaknesses that occur when an application does not properly handle errors that occur during processing. According to the authors of the Seven Pernicious Kingdoms, "Errors and error handling represent a class of API. Errors related to error handling are so common that they deserve a special kingdom of their own. As with ''API Abuse,'' there are two ways to introduce an error-related security vulnerability: the most common one is handling errors poorly (or not at all). The second is producing errors that either give out too much information (to possible attackers) or are difficult to handle."', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-389', N'Error Conditions, Return Values, Status Codes', NULL, N'Incomplete', N'This category includes weaknesses that occur if a function does not generate the correct return/status code, or if the application does not handle all possible return/status codes that could be generated by a function. This type of problem is most often found in conditions that are rarely encountered during the normal operation of the product. Presumably, most bugs related to common conditions are found and eliminated during development and testing. In some cases, the attacker can directly control or influence the environment to trigger the rare conditions.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-39', N'Path Traversal: ''C:dirname''', N'Variant', N'Draft', N'An attacker can inject a drive letter or Windows volume letter (''C:dirname'') into a software system to potentially redirect access to an unintended location or arbitrary file.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-390', N'Detection of Error Condition Without Action', N'Class', N'Draft', N'The software detects a specific error, but takes no actions to handle the error.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-391', N'Unchecked Error Condition', N'Base', N'Incomplete', N'[PLANNED FOR DEPRECATION. SEE MAINTENANCE NOTES.] Ignoring exceptions and other error conditions may allow an attacker to induce unexpected behavior unnoticed.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-392', N'Missing Report of Error Condition', N'Base', N'Draft', N'The software encounters an error but does not provide a status code or return value to indicate that an error has occurred.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-393', N'Return of Wrong Status Code', N'Base', N'Draft', N'A function or operation returns an incorrect return value or status code that does not indicate an error, but causes the product to modify its behavior based on the incorrect result.', N'This can lead to unpredictable behavior. If the function is used to make security-critical decisions or provide security-critical information, then the wrong status code can cause the software to assume that an action is safe, even when it is not.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-394', N'Unexpected Status Code or Return Value', N'Base', N'Draft', N'The software does not properly check when a function or operation returns a value that is legitimate for the function, but is not expected by the software.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-395', N'Use of NullPointerException Catch to Detect NULL Pointer Dereference', N'Base', N'Draft', N'Catching NullPointerException should not be used as an alternative to programmatic checks to prevent dereferencing a null pointer.', N'Programmers typically catch NullPointerException under three circumstances:
            
               
                  The program contains a null pointer dereference. Catching the resulting exception was easier than fixing the underlying problem.
                  The program explicitly throws a NullPointerException to signal an error condition.
                  The code is part of a test harness that supplies unexpected input to the classes under test.
               
            
            Of these three circumstances, only the last is acceptable.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-396', N'Declaration of Catch for Generic Exception', N'Base', N'Draft', N'Catching overly broad exceptions promotes complex error handling code that is more likely to contain security vulnerabilities.', N'Multiple catch blocks can get ugly and repetitive, but "condensing" catch blocks by catching a high-level class like Exception can obscure exceptions that deserve special treatment or that should not be caught at this point in the program. Catching an overly broad exception essentially defeats the purpose of Java''s typed exceptions, and can become particularly dangerous if the program grows and begins to throw new types of exceptions. The new exception types will not receive any attention.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-397', N'Declaration of Throws for Generic Exception', N'Base', N'Draft', N'Throwing overly broad exceptions promotes complex error handling code that is more likely to contain security vulnerabilities.', N'Declaring a method to throw Exception or Throwable makes it difficult for callers to perform proper error handling and error recovery. Java''s exception mechanism, for example, is set up to make it easy for callers to anticipate what can go wrong and write code to handle each specific exceptional circumstance. Declaring that a method throws a generic form of exception defeats this system.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-398', N'7PK - Code Quality', NULL, N'Draft', N'This category represents one of the phyla in the Seven Pernicious Kingdoms vulnerability classification. It includes weaknesses that do not directly introduce a weakness or vulnerability, but indicate that the product has not been carefully developed or maintained. According to the authors of the Seven Pernicious Kingdoms, "Poor code quality leads to unpredictable behavior. From a user''s perspective that often manifests itself as poor usability. For an adversary it provides an opportunity to stress the system in unexpected ways."', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-399', N'Resource Management Errors', NULL, N'Draft', N'Weaknesses in this category are related to improper management of system resources.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-4', N'J2EE Environment Issues', NULL, N'Incomplete', N'J2EE framework related environment issues with security implications.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-40', N'Path Traversal: ''\\UNC\share\name\'' (Windows UNC Share)', N'Variant', N'Draft', N'An attacker can inject a Windows UNC share (''\\UNC\share\name'') into a software system to potentially redirect access to an unintended location or arbitrary file.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-400', N'Uncontrolled Resource Consumption', N'Class', N'Incomplete', N'The software does not properly control the allocation and maintence of a limited resource thereby enabling an actor to influence the amount of resources consumed, eventually leading to the exhaustion of available resources.', N'Limited resources include memory, file system storage, database connection pool entries, and CPU. If an attacker can trigger the allocation of these limited resources, but the number or size of the resources is not controlled, then the attacker could cause a denial of service that consumes all available resources. This would prevent valid users from accessing the software, and it could potentially have an impact on the surrounding environment. For example, a memory exhaustion attack against an application could slow down the application as well as its host operating system.
            There are at least three distinct scenarios which can commonly lead to resource exhaustion:
            
               
                  Lack of throttling for the number of allocated recources
                  Losing all references to a resource before reaching the shutdown stage
                  Not closing/returning resource after processing
               
            
            Resource exhaustion problems are often result due to an incorrect implementation of the following situations:
            
               
                  Error conditions and other exceptional circumstances.
                  Confusion over which part of the program is responsible for releasing the resource.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-401', N'Missing Release of Memory after Effective Lifetime', N'Variant', N'Draft', N'The software does not sufficiently track and release allocated memory after it has been used, which slowly consumes remaining memory.', N'This is often triggered by improper handling of malformed data or unexpectedly interrupted sessions.  In some languages, developers are responsible for tracking memory allocation and releasing the memory.  If there are no more pointers or references to the memory, then it can no longer be tracked and identified for release.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-402', N'Transmission of Private Resources into a New Sphere (''Resource Leak'')', N'Class', N'Draft', N'The software makes resources available to untrusted parties when those resources are only intended to be accessed by the software.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-403', N'Exposure of File Descriptor to Unintended Control Sphere (''File Descriptor Leak'')', N'Base', N'Draft', N'A process does not close sensitive file descriptors before invoking a child process, which allows the child to perform unauthorized I/O operations using those descriptors.', N'When a new process is forked or executed, the child process inherits any open file descriptors. When the child process has fewer privileges than the parent process, this might introduce a vulnerability if the child process can access the file descriptor but does not have the privileges to access the associated file.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-404', N'Improper Resource Shutdown or Release', N'Class', N'Draft', N'The program does not release or incorrectly releases a resource before it is made available for re-use.', N'When a resource is created or allocated, the developer is responsible for properly releasing the resource as well as accounting for all potential paths of expiration or invalidation, such as a set period of time or revocation.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-405', N'Asymmetric Resource Consumption (Amplification)', N'Class', N'Incomplete', N'Software that does not appropriately monitor or control resource consumption can lead to adverse system performance.', N'This situation is amplified if the software allows malicious users or attackers to consume more resources than their access level permits. Exploiting such a weakness can lead to asymmetric resource consumption, aiding in amplification attacks against the system or the network.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-406', N'Insufficient Control of Network Message Volume (Network Amplification)', N'Class', N'Incomplete', N'The software does not sufficiently monitor or control transmitted network traffic volume, so that an actor can cause the software to transmit more traffic than should be allowed for that actor.', N'In the absence of a policy to restrict asymmetric resource consumption, the application or system cannot distinguish between legitimate transmissions and traffic intended to serve as an amplifying attack on target systems. Systems can often be configured to restrict the amount of traffic sent out on behalf of a client, based on the client''s origin or access level. This is usually defined in a resource allocation policy. In the absence of a mechanism to keep track of transmissions, the system or application can be easily abused to transmit asymmetrically greater traffic than the request or client should be permitted to.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-407', N'Inefficient Algorithmic Complexity', N'Class', N'Incomplete', N'An algorithm in a product has an inefficient worst-case computational complexity that may be detrimental to system performance and can be triggered by an attacker, typically using crafted manipulations that ensure that the worst case is being reached.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-408', N'Incorrect Behavior Order: Early Amplification', N'Base', N'Draft', N'The software allows an entity to perform a legitimate but expensive operation before authentication or authorization has taken place.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-409', N'Improper Handling of Highly Compressed Data (Data Amplification)', N'Base', N'Incomplete', N'The software does not handle or incorrectly handles a compressed input with a very high compression ratio that produces a large output.', N'An example of data amplification is a "decompression bomb," a small ZIP file that can produce a large amount of data when it is decompressed.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-41', N'Improper Resolution of Path Equivalence', N'Base', N'Incomplete', N'The system or application is vulnerable to file system contents disclosure through path equivalence. Path equivalence involves the use of special characters in file and directory names. The associated manipulations are intended to generate multiple names for the same object.', N'Path equivalence is usually employed in order to circumvent access controls expressed using an incomplete set of file name or file path representations. This is different from path traversal, wherein the manipulations are performed to generate a name for a different object.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-410', N'Insufficient Resource Pool', N'Base', N'Incomplete', N'The software''s resource pool is not large enough to handle peak demand, which allows an attacker to prevent others from accessing the resource by using a (relatively) large number of requests for resources.', N'Frequently the consequence is a "flood" of connection or sessions.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-411', N'Resource Locking Problems', NULL, N'Draft', N'Weaknesses in this category are related to improper handling of locks that are used to control access to resources.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-412', N'Unrestricted Externally Accessible Lock', N'Base', N'Incomplete', N'The software properly checks for the existence of a lock, but the lock can be externally controlled or influenced by an actor that is outside of the intended sphere of control.', N'This prevents the software from acting on associated resources or performing other behaviors that are controlled by the presence of the lock. Relevant locks might include an exclusive lock or mutex, or modifying a shared resource that is treated as a lock. If the lock can be held for an indefinite period of time, then the denial of service could be permanent.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-413', N'Improper Resource Locking', N'Base', N'Draft', N'The software does not lock or does not correctly lock a resource when the software must have exclusive access to the resource.', N'When a resource is not properly locked, an attacker could modify the resource while it is being operated on by the software. This might violate the software''s assumption that the resource will not change, potentially leading to unexpected behaviors.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-414', N'Missing Lock Check', N'Base', N'Draft', N'A product does not check to see if a lock is present before performing sensitive operations on a resource.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-415', N'Double Free', N'Variant', N'Draft', N'The product calls free() twice on the same memory address, potentially leading to modification of unexpected memory locations.', N'When a program calls free() twice with the same argument, the program''s memory management data structures become corrupted. This corruption can cause the program to crash or, in some circumstances, cause two later calls to malloc() to return the same pointer. If malloc() returns the same value twice and the program later gives the attacker control over the data that is written into this doubly-allocated memory, the program becomes vulnerable to a buffer overflow attack.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-416', N'Use After Free', N'Variant', N'Draft', N'Referencing memory after it has been freed can cause a program to crash, use unexpected values, or execute code.', N'The use of previously-freed memory can have any number of adverse consequences, ranging from the corruption of valid data to the execution of arbitrary code, depending on the instantiation and timing of the flaw. The simplest way data corruption may occur involves the system''s reuse of the freed memory. Use-after-free errors have two common and sometimes overlapping causes:
            
               
                  Error conditions and other exceptional circumstances.
                  Confusion over which part of the program is responsible for freeing the memory.
               
            
            In this scenario, the memory in question is allocated to another pointer validly at some point after it has been freed. The original pointer to the freed memory is used again and points to somewhere within the new allocation. As the data is changed, it corrupts the validly used memory; this induces undefined behavior in the process.
            If the newly allocated data chances to hold a class, in C++ for example, various function pointers may be scattered within the heap data. If one of these function pointers is overwritten with an address to valid shellcode, execution of arbitrary code can be achieved.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-417', N'Channel and Path Errors', NULL, N'Draft', N'Weaknesses in this category are related to improper handling of communication channels and access paths.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-418', N'DEPRECATED: Channel Errors', NULL, N'Deprecated', N'This category has been deprecated because it redundant with the grouping provided by CWE-417.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-419', N'Unprotected Primary Channel', N'Base', N'Draft', N'The software uses a primary channel for administration or restricted functionality, but it does not properly protect the channel.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-42', N'Path Equivalence: ''filename.'' (Trailing Dot)', N'Variant', N'Incomplete', N'A software system that accepts path input in the form of trailing dot (''filedir.'') without appropriate validation can lead to ambiguous path resolution and allow an attacker to traverse the file system to unintended locations or access arbitrary files.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-420', N'Unprotected Alternate Channel', N'Base', N'Draft', N'The software protects a primary channel, but it does not use the same level of protection for an alternate channel.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-421', N'Race Condition During Access to Alternate Channel', N'Base', N'Draft', N'The product opens an alternate channel to communicate with an authorized user, but the channel is accessible to other actors.', N'This creates a race condition that allows an attacker to access the channel before the authorized user does.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-422', N'Unprotected Windows Messaging Channel (''Shatter'')', N'Variant', N'Draft', N'The software does not properly verify the source of a message in the Windows Messaging System while running at elevated privileges, creating an alternate channel through which an attacker can directly send a message to the product.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-423', N'DEPRECATED (Duplicate): Proxied Trusted Channel', N'Base', N'Deprecated', N'This entry has been deprecated because it was a duplicate of CWE-441. All content has been transferred to CWE-441.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-424', N'Improper Protection of Alternate Path', N'Class', N'Draft', N'The product does not sufficiently protect all possible paths that a user can take to access restricted functionality or resources.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-425', N'Direct Request (''Forced Browsing'')', N'Base', N'Incomplete', N'The web application does not adequately enforce appropriate authorization on all restricted URLs, scripts, or files.', N'Web applications susceptible to direct request attacks often make the false assumption that such resources can only be reached through a given navigation path and so only apply authorization at certain points in the path.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-426', N'Untrusted Search Path', N'Base', N'Draft', N'The application searches for critical resources using an externally-supplied search path that can point to resources that are not under the application''s direct control.', N'This might allow attackers to execute their own programs, access unauthorized data files, or modify configuration in unexpected ways. If the application uses a search path to locate critical resources such as programs, then an attacker could modify that search path to point to a malicious program, which the targeted application would then execute. The problem extends to any type of critical resource that the application trusts.
            Some of the most common variants of untrusted search path are:
            
               
                  In various UNIX and Linux-based systems, the PATH environment variable may be consulted to locate executable programs, and LD_PRELOAD may be used to locate a separate library.
                  In various Microsoft-based systems, the PATH environment variable is consulted to locate a DLL, if the DLL is not found in other paths that appear earlier in the search order.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-427', N'Uncontrolled Search Path Element', N'Base', N'Draft', N'The product uses a fixed or controlled search path to find resources, but one or more locations in that path can be under the control of unintended actors.', N'Although this weakness can occur with any type of resource, it is frequently introduced when a product uses a directory search path to find executables or code libraries, but the path contains a directory that can be modified by an attacker, such as "/tmp" or the current working directory.
            In Windows-based systems, when the LoadLibrary or LoadLibraryEx function is called with a DLL name that does not contain a fully qualified path, the function follows a search order that includes two path elements that might be uncontrolled:
            
               
                  the directory from which the program has been loaded
                  the current working directory.
               
            
            In some cases, the attack can be conducted remotely, such as when SMB or WebDAV network shares are used.
            In some Unix-based systems, a PATH might be created that contains an empty element, e.g. by splicing an empty variable into the PATH. This empty element can be interpreted as equivalent to the current working directory, which might be an untrusted search element.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-428', N'Unquoted Search Path or Element', N'Base', N'Draft', N'The product uses a search path that contains an unquoted element, in which the element contains whitespace or other separators. This can cause the product to access resources in a parent path.', N'If a malicious individual has access to the file system, it is possible to elevate privileges by inserting such a file as "C:\Program.exe" to be run by a privileged program making use of WinExec.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-429', N'Handler Errors', NULL, N'Draft', N'Weaknesses in this category are related to improper management of handlers.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-43', N'Path Equivalence: ''filename....'' (Multiple Trailing Dot)', N'Variant', N'Incomplete', N'A software system that accepts path input in the form of multiple trailing dot (''filedir....'') without appropriate validation can lead to ambiguous path resolution and allow an attacker to traverse the file system to unintended locations or access arbitrary files.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-430', N'Deployment of Wrong Handler', N'Base', N'Incomplete', N'The wrong "handler" is assigned to process an object.', N'An example of deploying the wrong handler would be calling a servlet to reveal source code of a .JSP file, or automatically "determining" type of the object even if it is contradictory to an explicitly specified type.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-431', N'Missing Handler', N'Base', N'Draft', N'A handler is not available or implemented.', N'When an exception is thrown and not caught, the process has given up an opportunity to decide if a given failure or event is worth a change in execution.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-432', N'Dangerous Signal Handler not Disabled During Sensitive Operations', N'Base', N'Draft', N'The application uses a signal handler that shares state with other signal handlers, but it does not properly mask or prevent those signal handlers from being invoked while the original signal handler is still running.', N'During the execution of a signal handler, it can be interrupted by another handler when a different signal is sent. If the two handlers share state - such as global variables - then an attacker can corrupt the state by sending another signal before the first handler has completed execution.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-433', N'Unparsed Raw Web Content Delivery', N'Variant', N'Incomplete', N'The software stores raw content or supporting code under the web document root with an extension that is not specifically handled by the server.', N'If code is stored in a file with an extension such as ".inc" or ".pl", and the web server does not have a handler for that extension, then the server will likely send the contents of the file directly to the requester without the pre-processing that was expected. When that file contains sensitive information such as database credentials, this may allow the attacker to compromise the application or associated components.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-434', N'Unrestricted Upload of File with Dangerous Type', N'Base', N'Draft', N'The software allows the attacker to upload or transfer files of dangerous types that can be automatically processed within the product''s environment.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-435', N'Improper Interaction Between Multiple Correctly-Behaving Entities', N'Class', N'Draft', N'An interaction error occurs when two entities have correct behavior when running independently of each other, but when they are integrated as components in a larger system or process, they introduce incorrect behaviors that may cause resultant weaknesses.', N'When a system or process combines multiple independent components, this often produces new, emergent behaviors at the system level.  However, if the interactions between these components are not fully accounted for, some of the emergent behaviors can be incorrect or even insecure.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-436', N'Interpretation Conflict', N'Class', N'Incomplete', N'Product A handles inputs or steps differently than Product B, which causes A to perform incorrect actions based on its perception of B''s state.', N'This is generally found in proxies, firewalls, anti-virus software, and other intermediary devices that monitor, allow, deny, or modify traffic based on how the client or server is expected to behave.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-437', N'Incomplete Model of Endpoint Features', N'Base', N'Incomplete', N'A product acts as an intermediary or monitor between two or more endpoints, but it does not have a complete model of an endpoint''s features, behaviors, or state, potentially causing the product to perform incorrect actions based on this incomplete model.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-438', N'Behavioral Problems', NULL, N'Draft', N'Weaknesses in this category are related to unexpected behaviors from code that an application uses.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-439', N'Behavioral Change in New Version or Environment', N'Base', N'Draft', N'A''s behavior or functionality changes with a new version of A, or a new environment, which is not known (or manageable) by B.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-44', N'Path Equivalence: ''file.name'' (Internal Dot)', N'Variant', N'Incomplete', N'A software system that accepts path input in the form of internal dot (''file.ordir'') without appropriate validation can lead to ambiguous path resolution and allow an attacker to traverse the file system to unintended locations or access arbitrary files.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-440', N'Expected Behavior Violation', N'Base', N'Draft', N'A feature, API, or function being used by a product behaves differently than the product expects.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-441', N'Unintended Proxy or Intermediary (''Confused Deputy'')', N'Class', N'Draft', N'The software receives a request, message, or directive from an upstream component, but the software does not sufficiently preserve the original source of the request before forwarding the request to an external actor that is outside of the software''s control sphere. This causes the software to appear to be the source of the request, leading it to act as a proxy or other intermediary between the upstream component and the external actor.', N'If an attacker cannot directly contact a target, but the software has access to the target, then the attacker can send a request to the software and have it be forwarded from the target. The request would appear to be coming from the software''s system, not the attacker''s system. As a result, the attacker can bypass access controls (such as firewalls) or hide the source of malicious requests, since the requests would not be coming directly from the attacker.
            Since proxy functionality and message-forwarding often serve a legitimate purpose, this issue only becomes a vulnerability when:
            
               
                  The software runs with different privileges or on a different system, or otherwise has different levels of access than the upstream component;
                  The attacker is prevented from making the request directly to the target; and
                  The attacker can create a request that the proxy does not explicitly intend to be forwarded on the behalf of the requester. Such a request might point to an unexpected hostname, port number, or service. Or, the request might be sent to an allowed service, but the request could contain disallowed directives, commands, or resources.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-442', N'Web Problems', NULL, N'Draft', N'Weaknesses in this category are related to World Wide Web technology.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-443', N'DEPRECATED (Duplicate): HTTP response splitting', N'Base', N'Deprecated', N'This weakness can be found at CWE-113.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-444', N'Inconsistent Interpretation of HTTP Requests (''HTTP Request Smuggling'')', N'Base', N'Incomplete', N'When malformed or abnormal HTTP requests are interpreted by one or more entities in the data flow between the user and the web server, such as a proxy or firewall, they can be interpreted inconsistently, allowing the attacker to "smuggle" a request to one device without the other device being aware of it.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-445', N'DEPRECATED: User Interface Errors', NULL, N'Deprecated', N'This weakness has been deprecated because it was a duplicate of CWE-355. All content has been transferred to CWE-355.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-446', N'UI Discrepancy for Security Feature', N'Base', N'Incomplete', N'The user interface does not correctly enable or configure a security feature, but the interface provides feedback that causes the user to believe that the feature is in a secure state.', N'When the user interface does not properly reflect what the user asks of it, then it can lead the user into a false sense of security. For example, the user might check a box to enable a security option to enable encrypted communications, but the software does not actually enable the encryption. Alternately, the user might provide a "restrict ALL''" access control rule, but the software only implements "restrict SOME".', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-447', N'Unimplemented or Unsupported Feature in UI', N'Base', N'Draft', N'A UI function for a security feature appears to be supported and gives feedback to the user that suggests that it is supported, but the underlying functionality is not implemented.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-448', N'Obsolete Feature in UI', N'Base', N'Draft', N'A UI function is obsolete and the product does not warn the user.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-449', N'The UI Performs the Wrong Action', N'Base', N'Incomplete', N'The UI performs the wrong action with respect to the user''s request.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-45', N'Path Equivalence: ''file...name'' (Multiple Internal Dot)', N'Variant', N'Incomplete', N'A software system that accepts path input in the form of multiple internal dot (''file...dir'') without appropriate validation can lead to ambiguous path resolution and allow an attacker to traverse the file system to unintended locations or access arbitrary files.', NULL, NULL, NULL)
