INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-518', N'DEPRECATED: Inadvertently Introduced Weakness', NULL, N'Deprecated', N'This category has been deprecated as it was originally used for organizing the Development View (CWE-699), but it introduced unnecessary complexity and depth to the resulting tree.', NULL, NULL, NULL)
