INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-703', N'Improper Check or Handling of Exceptional Conditions', N'Class', N'Incomplete', N'The software does not properly anticipate or handle exceptional conditions that rarely occur during normal operation of the software.', NULL, NULL, NULL)
