INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-704', N'Incorrect Type Conversion or Cast', N'Class', N'Incomplete', N'The software does not correctly convert an object, resource, or structure from one type to a different type.', NULL, NULL, NULL)
