INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-705', N'Incorrect Control Flow Scoping', N'Class', N'Incomplete', N'The software does not properly return control flow to the proper location after it has completed a task or detected an unusual condition.', NULL, NULL, NULL)
