INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-706', N'Use of Incorrectly-Resolved Name or Reference', N'Class', N'Incomplete', N'The software uses a name or reference to access a resource, but the name/reference resolves to a resource that is outside of the intended control sphere.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-707', N'Improper Enforcement of Message or Data Structure', N'Class', N'Incomplete', N'The software does not enforce or incorrectly enforces that structured messages or data are well-formed before being read from an upstream component or sent to a downstream component.', N'If a message is malformed it may cause the message to be incorrectly interpreted.
            This weakness typically applies in cases where the product prepares a control message that another process must act on, such as a command or query, and malicious input that was intended as data, can enter the control plane instead. However, this weakness also applies to more general cases where there are not always control implications.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-708', N'Incorrect Ownership Assignment', N'Base', N'Incomplete', N'The software assigns an owner to a resource, but the owner is outside of the intended control sphere.', N'This may allow the resource to be manipulated by actors outside of the intended control sphere.', NULL, NULL)
