INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-73', N'External Control of File Name or Path', N'Class', N'Draft', N'The software allows user input to control or influence paths or file names that are used in filesystem operations.', N'This could allow an attacker to access or modify system files or other files that are critical to the application.
            Path manipulation errors occur when the following two conditions are met:
            
               1. An attacker can specify a path used in an operation on the filesystem.
               2. By specifying the resource, the attacker gains a capability that would not otherwise be permitted.
            
            For example, the program may give the attacker the ability to overwrite the specified file or run with a configuration controlled by the attacker.', NULL, NULL)
