INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-791', N'Incomplete Filtering of Special Elements', N'Base', N'Incomplete', N'The software receives data from an upstream component, but does not completely filter special elements before sending it to a downstream component.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-792', N'Incomplete Filtering of One or More Instances of Special Elements', N'Variant', N'Incomplete', N'The software receives data from an upstream component, but does not completely filter one or more instances of special elements before sending it to a downstream component.', N'Incomplete filtering of this nature involves either:
            
               
                  only filtering a single instance of a special element when more exist, or
                  not filtering all instances or all elements where multiple special elements exist.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-793', N'Only Filtering One Instance of a Special Element', N'Variant', N'Incomplete', N'The software receives data from an upstream component, but only filters a single instance of a special element before sending it to a downstream component.', N'Incomplete filtering of this nature may be location-dependent, as in only the first or last element is filtered.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-794', N'Incomplete Filtering of Multiple Instances of Special Elements', N'Variant', N'Incomplete', N'The software receives data from an upstream component, but does not filter all instances of a special element before sending it to a downstream component.', N'Incomplete filtering of this nature may be applied to:
            
               
                  sequential elements (special elements that appear next to each other) or
                  non-sequential elements (special elements that appear multiple times in different locations).', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-795', N'Only Filtering Special Elements at a Specified Location', N'Base', N'Incomplete', N'The software receives data from an upstream component, but only accounts for special elements at a specified location, thereby missing remaining special elements that may exist before sending it to a downstream component.', N'A filter might only account for instances of special elements when they occur:
            
               
                  relative to a marker (e.g. "at the beginning/end of string; the second argument"), or
                  at an absolute position (e.g. "byte number 10").
               
            
            This may leave special elements in the data that did not match the filter position, but still may be dangerous.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-796', N'Only Filtering Special Elements Relative to a Marker', N'Variant', N'Incomplete', N'The software receives data from an upstream component, but only accounts for special elements positioned relative to a marker (e.g. "at the beginning/end of a string; the second argument"), thereby missing remaining special elements that may exist before sending it to a downstream component.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-797', N'Only Filtering Special Elements at an Absolute Position', N'Variant', N'Incomplete', N'The software receives data from an upstream component, but only accounts for special elements at an absolute position (e.g. "byte number 10"), thereby missing remaining special elements that may exist before sending it to a downstream component.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-798', N'Use of Hard-coded Credentials', N'Base', N'Incomplete', N'The software contains hard-coded credentials, such as a password or cryptographic key, which it uses for its own inbound authentication, outbound communication to external components, or encryption of internal data.', N'Hard-coded credentials typically create a significant hole that allows an attacker to bypass the authentication that has been configured by the software administrator. This hole might be difficult for the system administrator to detect. Even if detected, it can be difficult to fix, so the administrator may be forced into disabling the product entirely. There are two main variations:
            
               Inbound: the software contains an authentication mechanism that checks the input credentials against a hard-coded set of credentials.
               Outbound: the software connects to another system or component, and it contains hard-coded credentials for connecting to that component.
            
            In the Inbound variant, a default administration account is created, and a simple password is hard-coded into the product and associated with that account. This hard-coded password is the same for each installation of the product, and it usually cannot be changed or disabled by system administrators without manually modifying the program, or otherwise patching the software. If the password is ever discovered or published (a common occurrence on the Internet), then anybody with knowledge of this password can access the product. Finally, since all installations of the software will have the same password, even across different organizations, this enables massive attacks such as worms to take place.
            The Outbound variant applies to front-end systems that authenticate with a back-end service. The back-end service may require a fixed password which can be easily discovered. The programmer may simply hard-code those back-end credentials into the front-end software. Any user of that program may be able to extract the password. Client-side systems with hard-coded passwords pose even more of a threat, since the extraction of a password from a binary is usually very simple.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-799', N'Improper Control of Interaction Frequency', N'Class', N'Incomplete', N'The software does not properly limit the number or frequency of interactions that it has with an actor, such as the number of incoming requests.', N'This can allow the actor to perform actions more frequently than expected. The actor could be a human or an automated process such as a virus or bot. This could be used to cause a denial of service, compromise program logic (such as limiting humans to a single vote), or other consequences. For example, an authentication routine might not limit the number of times an attacker can guess a password. Or, a web site might conduct a poll but only expect humans to vote a maximum of once a day.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-8', N'J2EE Misconfiguration: Entity Bean Declared Remote', N'Variant', N'Incomplete', N'When an application exposes a remote interface for an entity bean, it might also expose methods that get or set the bean''s data. These methods could be leveraged to read sensitive information, or to change data in ways that violate the application''s expectations, potentially leading to other vulnerabilities.', NULL, NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-80', N'Improper Neutralization of Script-Related HTML Tags in a Web Page (Basic XSS)', N'Variant', N'Incomplete', N'The software receives input from an upstream component, but it does not neutralize or incorrectly neutralizes special characters such as "<", ">", and "&" that could be interpreted as web-scripting elements when they are sent to a downstream component that processes web pages.', N'This may allow such characters to be treated as control characters, which are executed client-side in the context of the user''s session. Although this can be classified as an injection problem, the more pertinent issue is the improper conversion of such special characters to respective context-appropriate entities before displaying them to the user.', NULL, NULL)
GO
