INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-804', N'Guessable CAPTCHA', N'Base', N'Incomplete', N'The software uses a CAPTCHA challenge, but the challenge can be guessed or automatically recognized by a non-human actor.', N'An automated attacker could bypass the intended protection of the CAPTCHA challenge and perform actions at a higher frequency than humanly possible, such as launching spam attacks.
            There can be several different causes of a guessable CAPTCHA:
            
               
                  An audio or visual image that does not have sufficient distortion from the unobfuscated source image.
                  A question is generated that with a format that can be automatically recognized, such as a math question.
                  A question for which the number of possible answers is limited, such as birth years or favorite sports teams.
                  A general-knowledge or trivia question for which the answer can be accessed using a data base, such as country capitals or popular actors.
                  Other data associated with the CAPTCHA may provide hints about its contents, such as an image whose filename contains the word that is used in the CAPTCHA.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-805', N'Buffer Access with Incorrect Length Value', N'Base', N'Incomplete', N'The software uses a sequential operation to read or write a buffer, but it uses an incorrect length value that causes it to access memory that is outside of the bounds of the buffer.', N'When the length value exceeds the size of the destination, a buffer overflow could occur.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-806', N'Buffer Access Using Size of Source Buffer', N'Variant', N'Incomplete', N'The software uses the size of a source buffer when reading from or writing to a destination buffer, which may cause it to access memory that is outside of the bounds of the buffer.', N'When the size of the destination is smaller than the size of the source, a buffer overflow could occur.', NULL, NULL)
GO
INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-807', N'Reliance on Untrusted Inputs in a Security Decision', N'Base', N'Incomplete', N'The application uses a protection mechanism that relies on the existence or values of an input, but the input can be modified by an untrusted actor in a way that bypasses the protection mechanism.', N'Developers may assume that inputs such as cookies, environment variables, and hidden form fields cannot be modified. However, an attacker could change these inputs using customized clients or other attacks. This change might not be detected. When security decisions such as authentication and authorization are made based on the values of these inputs, attackers can bypass the security of the software.
            Without sufficient encryption, integrity checking, or other mechanism, any input that originates from an outsider cannot be trusted.', NULL, NULL)
GO
