INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-81', N'Improper Neutralization of Script in an Error Message Web Page', N'Variant', N'Incomplete', N'The software receives input from an upstream component, but it does not neutralize or incorrectly neutralizes special characters that could be interpreted as web-scripting elements when they are sent to an error page.', N'Error pages may include customized 403 Forbidden or 404 Not Found pages.
            When an attacker can trigger an error that contains unneutralized input, then cross-site scripting attacks may be possible.', NULL, NULL)
