INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-908', N'Use of Uninitialized Resource', N'Base', N'Incomplete', N'The software uses a resource that has not been properly initialized.', N'This can have security implications when the associated resource is expected to have certain properties or values.', NULL, NULL)
