INSERT [dbo].[icsa_cwe] ([cwe_id], [name], [weakness_abstraction], [status], [description], [extended_description], [background_details], [comments]) VALUES (N'CWE-909', N'Missing Initialization of Resource', N'Base', N'Incomplete', N'The software does not initialize a critical resource.', N'Many resources require initialization before they can be properly used. If a resource is not initialized, it could contain unpredictable or expired data, or it could be initialized to defaults that are invalid. This can have security implications when the resource is expected to have certain properties or values.', NULL, NULL)
